def predict(triples: list[Triple]) -> str:
    """
    Generates a natural language sentence from a list of triples.
    """
    if not triples:
        return ""

    sentence = ""
    subject = triples[0].subject

    for i in range(len(triples)):
        triple = triples[i]
        predicate = triple.predicate
        object_value = triple.object

        if predicate == "country" and i + 1 < len(triples) and triples[i + 1].predicate == "capital":
            sentence += f"{subject} is in {object_value}, and its capital is {triples[i + 1].object}. "
            i += 1 # Skip the next triple as it's already used
        elif predicate == "industry" and i + 1 < len(triples) and triples[i + 1].predicate == "revenue":
            sentence += f"{subject} operates in the {object_value} industry, with a revenue of {triples[i + 1].object}. "
            i += 1
        elif predicate == "netIncome" and i + 1 < len(triples) and triples[i + 1].predicate == "numberOfEmployees":
             sentence += f"The net income of {subject} is {object_value}, and it employs {triples[i + 1].object} people."
             i += 1
        else:
            sentence += f"{subject} {predicate} {object_value}. "

    return sentence.strip()

