            sentences.append(f"The elevation of {subject} is {object_value}.")
        else:
            sentences.append(f"The {predicate} of {subject} is {object_value}.")

    if not sentences:
        return ""

    # Combine sentences where possible for better flow, prioritizing same subject
    combined_sentences = []
    current_sentence = ""
    current_subject = None

    for sentence in sentences:
        parts = sentence.split()
        subject = parts[0]  # Assume first word is the subject

        if current_subject is None:
            current_subject = subject
            current_sentence = sentence
        elif subject == current_subject and len(current_sentence) + len(sentence) + 2 <= 150:
            current_sentence += ", " + sentence
        else:
            combined_sentences.append(current_sentence)
            current_sentence = sentence
            current_subject = subject

    combined_sentences.append(current_sentence)

    return " ".join(combined_sentences)

