from dataclasses import dataclass

@dataclass
class Triple:
    subject: str
    predicate: str
    object: str

# EVOLVE-BLOCK-START

def predict(triples: list[Triple]) -> str:
    sentence = ""
    if not triples:
        return sentence

    subject = triples[0].subject
    sentence += f"{subject} "

    clauses = []
    for triple in triples:
        predicate = triple.predicate
        object_val = triple.object

        if predicate == "country":
            clauses.append(f"is in {object_val}")
        elif predicate == "ingredient":
            clauses.append(f"contains {object_val}")
        elif predicate == "region":
            clauses.append(f"is from the {object_val} region")
        elif predicate == "capital":
            clauses.append(f"where the capital is {object_val}")
        elif predicate == "served":
            clauses.append(f"is served {object_val}")
        elif predicate == "mainIngredient":
            clauses.append(f"is made with {object_val}")
        elif predicate == "dishVariation":
            clauses.append(f"which has a variation of {object_val}")
        elif predicate == "creator":
            clauses.append(f"created by {object_val}")
        elif predicate == "ethnicGroup":
            clauses.append(f"and is associated with the {object_val}")
        elif predicate == "language":
            clauses.append(f"and the language spoken is {object_val}")
        elif predicate == "leader":
            clauses.append(f"and its leader is {object_val}")
        else:
            if predicate == "alternativeName":
                clauses.append(f"also known as {object_val}")
            elif predicate == "related":
                clauses.append(f"which is related to {object_val}")
            elif predicate == "similarDish":
                clauses.append(f"similar to {object_val}")
            elif predicate == "creatorOfDish":
                clauses.append(f"created by {object_val}")
            else:
                clauses.append(f"and its {predicate} is {object_val}")

    if not clauses:
        return sentence.strip() + "."

    grouped_clauses = []
    current_group = []
    for clause in clauses:
        if len(current_group) < 2:
            current_group.append(clause)
        else:
            grouped_clauses.append(", ".join(current_group))
            current_group = [clause]
    if current_group:
        grouped_clauses.append(", ".join(current_group))

    if len(grouped_clauses) == 1:
        sentence += grouped_clauses[0] + "."
    else:
        sentence += ", ".join(grouped_clauses[:-1]) + ", and " + grouped_clauses[-1] + "."
    return sentence.strip()

# EVOLVE-BLOCK-END

