def predict(triples: list[Triple]) -> str:
    sentence = ""
    clauses = []
    for i, triple in enumerate(triples):
        subject = triple.subject
        predicate = triple.predicate
        object_ = triple.object

        if predicate == "city":
            clause = f"{subject} is a city in {object_}"
        elif predicate == "country":
            clause = f"{subject} is located in {object_}"
        elif predicate == "capital":
            clause = f"{object_} is the capital of {subject}"
        elif predicate == "latinName":
            clause = f"The Latin name for {subject} is {object_}"
        elif predicate == "nickname":
            clause = f"{subject} is also known as {object_}"
        elif predicate == "rector":
            clause = f"The rector of {subject} is {object_}"
        elif predicate == "academicStaffSize":
            clause = f"{subject} has an academic staff of {object_}"
        elif predicate == "established":
            clause = f"{subject} was established in {object_}"
        elif predicate == "state":
            clause = f"{subject} is in the state of {object_}"
        else:
            clause = f"The {predicate} of {subject} is {object_}"
        clauses.append(clause)

    sentence = ", ".join(clauses) + "."
    return sentence

