def predict(triples: list[Triple]) -> str:
    clauses = []
    airport = None
    location = None
    country = None
    capital = None

    for triple in triples:
        if triple.predicate == "cityServed":
            if airport is None:
                airport = triple.subject
            clauses.append(f"{airport} serves {triple.object}")
        elif triple.predicate == "country":
            country = triple.object
            clauses.append(f"{triple.subject} is in {country}")
        elif triple.predicate == "capital":
            capital = triple.object
            clauses.append(f"The capital of {triple.subject} is {capital}")
        elif triple.predicate == "elevationAboveTheSeaLevel":
            clauses.append(f"{triple.subject} has an elevation of {triple.object} meters")
        elif triple.predicate == "location":
            location = triple.object
            clauses.append(f"{triple.subject} is located in {location}")
        else:
            clauses.append(f"{triple.subject} {triple.predicate} {triple.object}")

    sentence = ""
    if airport:
        sentence += f"{airport} "
        for clause in clauses:
            if "serves" in clause:
                sentence += f"serves {clause.split('serves ')[1]}. "
            elif "is in" in clause:
                sentence += f"is located in {clause.split('is in ')[1]}. "
            elif "capital" in clause:
                sentence += f"where {clause}. "
            elif "elevation" in clause:
                sentence += f"which has an elevation of {clause.split('elevation of ')[1]} meters. "
            elif "located in" in clause:
                sentence += f"and is located in {clause.split('located in ')[1]}. "
            else:
                sentence += f"and {clause}. "
    else:
        sentence = " and ".join(clauses) + "."

    return sentence

