                discoverer_prop = next((p for p in sentence_parts if "discoverer" in p), None)
                epoch_prop = next((p for p in sentence_parts if "epoch" in p), None)
                sentence = f"{subject} was discovered by {discoverer_prop.split('was discovered by ')[1]}, which has an epoch of {epoch_prop.split('has an epoch of ')[1]}."
            elif "birthDate" in sentence_parts and "deathDate" in sentence_parts:
                birth_date_prop = next((p for p in sentence_parts if "birthDate" in p), None)
                death_date_prop = next((p for p in sentence_parts if "deathDate" in p), None)
                sentence = f"{subject} was born on {birth_date_prop.split('was born on ')[1]} and died on {death_date_prop.split('died on ')[1]}."
            elif "formerName" in sentence_parts and "epoch" in sentence_parts:
                former_name_prop = next((p for p in sentence_parts if "formerName" in p), None)
                epoch_prop = next((p for p in sentence_parts if "epoch" in p), None)
                sentence = f"{subject}, formerly known as {former_name_prop.split('was formerly known as ')[1]}, has an epoch of {epoch_prop.split('has an epoch of ')[1]}."
            else:
                sentence = f"{subject} {', '.join(sentence_parts[:-1]) + ' and ' + sentence_parts[-1]}."
        else:
            sentence = f"{subject} {properties[0]}."
        sentences.append(sentence)

    if len(sentences) == 1:
        return sentences[0]
    else:
        return " ".join(sentences)

