from dataclasses import dataclass

@dataclass
class Triple:
    subject: str
    predicate: str
    object: str

# EVOLVE-BLOCK-START

def predict(triples: list[Triple]) -> str:
    if not triples:
        return ""

    sentence = ""
    for i, triple in enumerate(triples):
        if triple.predicate == "alternativeName":
            sentence += f"{triple.subject} is also known as {triple.object}."
        elif triple.predicate == "creator":
            sentence += f"{triple.subject} was created by {triple.object}."
        elif triple.predicate == "fullName":
            sentence += f"{triple.subject}'s full name is {triple.object}."
        elif triple.predicate == "foundedBy":
            sentence += f"{triple.subject} was founded by {triple.object}."
        elif triple.predicate == "keyPerson":
            sentence += f"{triple.object} is a key person at {triple.subject}."
        elif triple.predicate == "city":
            sentence += f"{triple.subject} is located in {triple.object}."
        elif triple.predicate == "product":
            sentence += f"{triple.subject} produces {triple.object}."
        elif triple.predicate == "lastAired":
            sentence += f"{triple.subject} last aired on {triple.object}."
        elif triple.predicate == "starring":
            sentence += f"{triple.subject} stars {triple.object}."
        elif triple.predicate == "firstAppearanceInFilm":
            sentence += f"{triple.subject} first appeared in {triple.object}."
        elif triple.predicate == "voice":
            sentence += f"{triple.subject} is voiced by {triple.object}."
        elif triple.predicate == "distributor":
            sentence += f"{triple.object} distributes {triple.subject}."
        elif triple.predicate == "birthPlace":
            sentence += f"{triple.subject} was born in {triple.object}."
        elif triple.predicate == "nationality":
            sentence += f"{triple.subject} is of {triple.object} nationality."
        elif triple.predicate == "award":
            sentence += f"{triple.subject} won the {triple.object}."
        elif triple.predicate == "broadcastedBy":
            sentence += f"{triple.subject} is broadcasted by {triple.object}."
        elif triple.predicate == "firstAired":
            sentence += f"{triple.subject} first aired on {triple.object}."
        elif triple.predicate == "series":
            sentence += f"{triple.subject} is part of the {triple.object} series."
        elif triple.predicate == "child":
            sentence += f"{triple.subject}'s child is {triple.object}."
        else:
            sentence += f"The {triple.predicate} of {triple.subject} is {triple.object}."

        if i < len(triples) - 1:
            sentence += " "

    return sentence

