    sentences = []
    for triple in triples:
        if triple.predicate == "category":
            sentences.append(f"{triple.subject} is a {triple.object}.")
        elif triple.predicate == "country":
            sentences.append(f"{triple.subject} is in {triple.object}.")
        elif triple.predicate == "location":
            sentences.append(f"{triple.subject} is located in {triple.object}.")
        elif triple.predicate == "municipality":
            sentences.append(f"{triple.subject} is in the municipality of {triple.object}.")
        elif triple.predicate == "state":
            sentences.append(f"{triple.subject} is in the state of {triple.object}.")
        elif triple.predicate == "district":
            sentences.append(f"{triple.subject} is in the {triple.object}.")
        elif triple.predicate == "established" or triple.predicate == "foundingDate":
            sentences.append(f"{triple.subject} was established on {triple.object}.")
        elif triple.predicate == "owner" or triple.predicate == "owningOrganisation":
            sentences.append(f"{triple.subject} is owned by {triple.object}.")
        elif triple.predicate == "hasToItsNorth" or triple.predicate == "hasToItsSouthwest" or triple.predicate == "hasToItsWest" or triple.predicate == "hasToItsSoutheast":
            sentences.append(f"{triple.subject} has {triple.predicate.replace('hasToIts', '')} {triple.object}.")
        elif triple.predicate == "inaugurationDate":
            sentences.append(f"{triple.subject} was inaugurated on {triple.object}.")
        elif triple.predicate == "capital":
            sentences.append(f"{triple.object} is the capital of {triple.subject}.")
        elif triple.predicate == "dedicatedTo":
            sentences.append(f"{triple.subject} is dedicated to {triple.object}.")
        elif triple.predicate == "designer":
            sentences.append(f"{triple.subject} was designed by {triple.object}.")
        elif triple.predicate == "material":
            sentences.append(f"{triple.subject} is made of {triple.object}.")
        elif triple.predicate == "language":
            sentences.append(f"{triple.subject} speaks {triple.object}.")
        elif triple.predicate == "religion":
            sentences.append(f"{triple.subject} follows {triple.object}.")
        elif triple.predicate == "nearestCity":
            sentences.append(f"{triple.subject} is near {triple.object}.")
        elif triple.predicate == "currency":
            sentences.append(f"{triple.subject} uses {triple.object}.")
        elif triple.predicate == "largestCity":
            sentences.append(f"{triple.subject}'s largest city is {triple.object}.")
        elif triple.predicate == "leader":
            sentences.append(f"{triple.subject}'s leader is {triple.object}.")
        elif triple.predicate == "leaderTitle":
            sentences.append(f"{triple.object} is the leader of {triple.subject}.")
        elif triple.predicate == "nativeName":
            sentences.append(f"The native name for {triple.subject} is {triple.object}.")
        else:
            sentences.append(f"The {triple.predicate} of {triple.subject} is {triple.object}.")

    if not sentences:
        return ""

    if len(sentences) == 1:
        return sentences[0]

    # Attempt to combine sentences for better flow
    combined_sentence = sentences[0]
    for i in range(1, len(sentences)):
        if "is in" in sentences[i] or "is located in" in sentences[i] or "was designed by" in sentences[i] or "is made of" in sentences[i]:
            combined_sentence += ", which " + sentences[i]
        else:
            combined_sentence += " and " + sentences[i]

    return combined_sentence + "."

