from dataclasses import dataclass

@dataclass
class Triple:
    subject: str
    predicate: str
    object: str

# EVOLVE-BLOCK-START

def predict(triples: list[Triple]) -> str:
    """
    Converts a list of triples into a natural language sentence.
    """
    sentence = ""
    subject = ""
    for i, triple in enumerate(triples):
        if i == 0:
            subject = triple.subject
            if triple.predicate == "city":
                sentence += f"{subject} is a city in {triple.object}."
            elif triple.predicate == "country":
                sentence += f"{subject} is located in {triple.object}."
            elif triple.predicate == "latinName":
                sentence += f"{subject} is also known as {triple.object}."
            elif triple.predicate == "nickname":
                sentence += f"{subject} is nicknamed {triple.object}."
            elif triple.predicate == "rector":
                sentence += f"The rector of {subject} is {triple.object}."
            elif triple.predicate == "academicStaffSize":
                sentence += f"{subject} has an academic staff of {triple.object}."
            elif triple.predicate == "established":
                sentence += f"{subject} was established in {triple.object}."
            elif triple.predicate == "state":
                sentence += f"{subject} is located in the state of {triple.object}."
            elif triple.predicate == "dean":
                sentence += f"The dean of {subject} is {triple.object}."
            elif triple.predicate == "location":
                sentence += f"{subject} is located in {triple.object}."
            elif triple.predicate == "numberOfStudents":
                sentence += f"{subject} has {triple.object} students."
            elif triple.predicate == "affiliation":
                sentence += f"{subject} is affiliated with {triple.object}."
            elif triple.predicate == "director":
                sentence += f"The director of {subject} is {triple.object}."
            elif triple.predicate == "motto":
                sentence += f"The motto of {subject} is \"{triple.object}\"."
            elif triple.predicate == "numberOfPostgraduateStudents":
                sentence += f"{subject} has {triple.object} postgraduate students."
            elif triple.predicate == "numberOfUndergraduateStudents":
                sentence += f"{subject} has {triple.object} undergraduate students."
            elif triple.predicate == "officialSchoolColour":
                sentence += f"The official school colour of {subject} is {triple.object}."
            elif triple.predicate == "outlookRanking":
                sentence += f"{subject} has an Outlook ranking of {triple.object}."
            elif triple.predicate == "sportsOffered":
                sentence += f"{subject} offers {triple.object}."
            elif triple.predicate == "wasGivenTheTechnicalCampusStatusBy":
                sentence += f"{subject} was given the technical campus status by {triple.object}."
            elif triple.predicate == "founder":
                sentence += f"{subject} was founded by {triple.object}."
            elif triple.predicate == "leader":
                sentence += f"The leader of {subject} is {triple.object}."
            elif triple.predicate == "leaderTitle":
                sentence += f"The leader title of {subject} is {triple.object}."
            elif triple.predicate == "religion":
                sentence += f"The religion of {subject} is {triple.object}."
            elif triple.predicate == "headquarter":
                sentence += f"The headquarter of {subject} is {triple.object}."
            elif triple.predicate == "largestCity":
                sentence += f"The largest city of {subject} is {triple.object}."
            elif triple.predicate == "river":
                sentence += f"{subject} has the river {triple.object}."
            elif triple.predicate == "hasToItsWest":
                sentence += f"{subject} has {triple.object} to its west."
            elif triple.predicate == "anthem":
                sentence += f"The anthem of {subject} is {triple.object}."
            elif triple.predicate == "ethnicGroup":
                sentence += f"An ethnic group of {subject} is {triple.object}."
            elif triple.predicate == "legislature":
                sentence += f"The legislature of {subject} is {triple.object}."
            elif triple.predicate == "sportGoverningBody":
                sentence += f"The sport governing body of {subject} is {triple.object}."
            elif triple.predicate == "isPartOf":
                sentence += f"{subject} is part of {triple.object}."
            elif triple.predicate == "neighboringMunicipality":
                sentence += f"{subject} has {triple.object} as a neighboring municipality."
            elif triple.predicate == "capital":
                sentence += f"{subject} is the capital of {triple.object}."
            elif triple.predicate == "patronSaint":
                sentence += f"The patron saint of {subject} is {triple.object}."
            elif triple.predicate == "hasToItsNorthwest":
                sentence += f"{subject} has {triple.object} to its northwest."
            elif triple.predicate == "campus":
                sentence += f"The campus of {subject} is {triple.object}."
            elif triple.predicate == "president":
                sentence += f"The president of {subject} is {triple.object}."
            elif triple.predicate == "staff":
                sentence += f"{subject} has a staff of {triple.object}."
            elif triple.predicate == "numberOfDoctoralStudents":
                sentence += f"{subject} has {triple.object} doctoral students."
            elif triple.predicate == "elevationAboveTheSeaLevel":
                sentence += f"{subject} has an elevation above sea level of {triple.object}."
            elif triple.predicate == "postalCode":
                sentence += f"The postal code of {subject} is {triple.object}."
            elif triple.predicate == "longName":
                sentence += f"{subject} has a long name of {triple.object}."

            else:
                sentence += f"{subject} {triple.predicate} {triple.object}."

        else:
            if triple.predicate == "city" and any(t.predicate == "country" for t in triples[:i]):
                sentence += f", which is located in {triple.object}."
            elif triple.predicate == "country":
                sentence += f", located in {triple.object}."
            elif triple.predicate == "established":
                sentence += f", established in {triple.object}."
            else:
                sentence += f" and {triple.predicate} is {triple.object}."
    return sentence

