from dataclasses import dataclass

@dataclass
class Triple:
    subject: str
    predicate: str
    object: str

# EVOLVE-BLOCK-START

def predict(triples: list[Triple]) -> str:
    airport = None
    city = None
    country = None
    elevation = None
    runway_length = None
    iata = None
    location = None
    operating_organisation = None
    runway_name = None

    for triple in triples:
        if triple.predicate == "cityServed":
            city = triple.object
        elif triple.predicate == "country":
            country = triple.object
        elif triple.predicate == "elevationAboveTheSeaLevel":
            elevation = triple.object
        elif triple.predicate == "runwayLength":
            runway_length = triple.object
        elif triple.predicate == "iataLocationIdentifier":
            iata = triple.object
        elif triple.predicate == "location":
            location = triple.object
        elif triple.predicate == "nativeName":
            airport = triple.object
        elif triple.predicate == "operatingOrganisation":
            operating_organisation = triple.object
        elif triple.predicate == "runwayName":
            runway_name = triple.object
        else:
            airport = triple.subject if airport is None else airport

    sentence = ""
    if airport:
        sentence += f"{airport}"

    if operating_organisation:
        sentence += f" is operated by {operating_organisation}"

    if location:
        sentence += f" is located in {location}"

    if city:
        sentence += f", serving the city of {city}"

    if country:
        sentence += f", which is in {country}"

    if elevation:
        sentence += f" and has an elevation of {elevation}"

    if runway_length:
        sentence += f" with a runway length of {runway_length}"

    if runway_name:
        sentence += f" and runway {runway_name}"

    if iata:
        sentence += f" with IATA code {iata}"

    return sentence + "."

