from dataclasses import dataclass

@dataclass
class Triple:
    subject: str
    predicate: str
    object: str

# EVOLVE-BLOCK-START

def predict(triples: list[Triple]) -> str:
    """
    Generates a natural language sentence from a list of triples.
    """
    sentence = ""
    airport = None
    for triple in triples:
        if triple.predicate == "cityServed":
            if not sentence:
                sentence += f"{triple.subject} serves the city of {triple.object}. "
            else:
                sentence += f"It also serves the city of {triple.object}. "
        elif triple.predicate == "country":
            if not sentence:
                sentence += f"{triple.subject} is located in {triple.object}. "
            else:
                sentence += f"It is in {triple.object}. "
        elif triple.predicate == "elevationAboveTheSeaLevel":
            sentence += f"The elevation is {triple.object} meters. "
        elif triple.predicate == "runwayLength":
            sentence += f"The runway length is {triple.object} meters. "
        elif triple.predicate == "iataLocationIdentifier":
            sentence += f"The IATA code is {triple.object}. "
        elif triple.predicate == "location":
            if not sentence:
                sentence += f"{triple.subject} is located in {triple.object}. "
            else:
                sentence += f"It's located in {triple.object}. "
        elif triple.predicate == "nativeName":
            airport = triple.object
            if not sentence:
                sentence += f"{airport} is an airport. "
        elif triple.predicate == "operatingOrganisation":
            sentence += f"It is operated by {triple.object}. "
        else:
            pass

    return sentence.strip()

