from dataclasses import dataclass

@dataclass
class Triple:
    subject: str
    predicate: str
    object: str

# EVOLVE-BLOCK-START

def predict(triples: list[Triple]) -> str:
    """
    Generates a natural language sentence from a list of triples.
    """
    sentence = ""
    airport = None
    locations = []
    for triple in triples:
        if triple.predicate == "cityServed":
            locations.append(f"{triple.subject} serves the city of {triple.object}")
        elif triple.predicate == "country":
            locations.append(f"{triple.subject} is located in {triple.object}")
        elif triple.predicate == "elevationAboveTheSeaLevel":
            locations.append(f"The elevation of {triple.subject} is {triple.object} meters")
        elif triple.predicate == "runwayLength":
            locations.append(f"The runway length of {triple.subject} is {triple.object} meters")
        elif triple.predicate == "iataLocationIdentifier":
            locations.append(f"The IATA code for {triple.subject} is {triple.object}")
        elif triple.predicate == "location":
            locations.append(f"{triple.subject} is located in {triple.object}")
        elif triple.predicate == "nativeName":
            airport = triple.object
            locations.append(f"{airport} is the native name of the airport.")
        elif triple.predicate == "operatingOrganisation":
            locations.append(f"{triple.subject} is operated by {triple.object}")
        elif triple.predicate == "runwayName":
            locations.append(f"{triple.subject} has a runway named {triple.object}")
        elif triple.predicate == "1stRunwayLengthFeet":
            locations.append(f"The first runway length of {triple.subject} is {triple.object} feet")
        elif triple.predicate == "1stRunwaySurfaceType":
            locations.append(f"The first runway surface type of {triple.subject} is {triple.object}")
        elif triple.predicate == "1stRunwayNumber":
            locations.append(f"The first runway number of {triple.subject} is {triple.object}")
        elif triple.predicate == "elevationAboveTheSeaLevelInFeet":
            locations.append(f"The elevation of {triple.subject} is {triple.object} feet")
        elif triple.predicate == "elevationAboveTheSeaLevelInMetres":
            locations.append(f"The elevation of {triple.subject} is {triple.object} metres")
        else:
            pass

    sentence = ", ".join(locations)
    return sentence.strip()

