from dataclasses import dataclass

@dataclass
class Triple:
    subject: str
    predicate: str
    object: str

# EVOLVE-BLOCK-START

def predict(triples: list[Triple]) -> str:
    """
    Generates a sentence from a list of triples related to airports.
    """
    sentence = ""
    airport = None
    location_info = []
    country_info = None
    runway_info = []

    for triple in triples:
        if triple.predicate == "cityServed":
            location_info.append(f"serves the city of {triple.object}")
        elif triple.predicate == "country":
            country_info = triple.object
        elif triple.predicate == "elevationAboveTheSeaLevel":
            location_info.append(f"has an elevation of {triple.object} above sea level")
        elif triple.predicate == "runwayLength":
            runway_info.append(f"has a runway length of {triple.object} meters")
        elif triple.predicate == "iataLocationIdentifier":
            location_info.append(f"has an IATA identifier of {triple.object}")
        elif triple.predicate == "location":
            location_info.append(f"is located in {triple.object}")
        elif triple.predicate == "operatingOrganisation":
            location_info.append(f"is operated by {triple.object}")
        elif triple.predicate == "hubAirport":
            location_info.append(f"is a hub for {triple.object}")
        elif triple.predicate == "headquarter":
            location_info.append(f"has its headquarters in {triple.object}")
        elif triple.predicate == "nativeName":
            airport = triple.object
        elif triple.predicate == "runwayName":
            runway_info.append(f"has a runway named {triple.object}")
        elif triple.predicate == "capital":
            location_info.append(f"the capital is {triple.object}")
        elif triple.predicate == "1stRunwayLengthFeet":
            runway_info.append(f"has a first runway length of {triple.object} feet")
        else:
            pass

    if airport:
        sentence += f"{airport} "

    if country_info:
        sentence += f"is in {country_info} and "

    if location_info:
        sentence += ", ".join(location_info) + ", "

    if runway_info:
        sentence += ", ".join(runway_info)

    return sentence.strip() + "."

