from dataclasses import dataclass

@dataclass
class Triple:
    subject: str
    predicate: str
    object: str

# EVOLVE-BLOCK-START

def predict(triples: list[Triple]) -> str:
    """
    Generates a sentence from a list of triples related to airports.
    """
    sentence_parts = []
    airport_name = None
    location_info = []
    country_info = None
    other_info = []

    for triple in triples:
        subject = triple.subject
        predicate = triple.predicate
        object_val = triple.object

        if predicate == "cityServed":
            location_info.append(f"{subject} serves {object_val}")
        elif predicate == "country":
            country_info = object_val
        elif predicate == "elevationAboveTheSeaLevel":
            location_info.append(f"{subject} is at an elevation of {object_val} meters")
        elif predicate == "runwayLength":
            location_info.append(f"{subject} has a runway length of {object_val} meters")
        elif predicate == "iataLocationIdentifier":
            location_info.append(f"The IATA code for {subject} is {object_val}")
        elif predicate == "location":
            location_info.append(f"{subject} is located in {object_val}")
        elif predicate == "operatingOrganisation":
            other_info.append(f"{subject} is operated by {object_val}")
        elif predicate == "hubAirport":
            other_info.append(f"{subject} is a hub for {object_val}")
        elif predicate == "headquarter":
            other_info.append(f"{subject}'s headquarters are in {object_val}")
        elif predicate == "nativeName":
            airport_name = object_val
        elif predicate == "runwayName":
            location_info.append(f"{subject} has a runway named {object_val}")
        elif predicate == "1stRunwayLengthFeet":
            location_info.append(f"{subject}'s first runway is {object_val} feet long")
        elif predicate == "1stRunwaySurfaceType":
            location_info.append(f"{subject}'s first runway surface is {object_val}")
        elif predicate == "3rdRunwayLengthFeet":
            location_info.append(f"{subject}'s third runway is {object_val} feet long")
        elif predicate == "icaoLocationIdentifier":
            location_info.append(f"{subject}'s ICAO identifier is {object_val}")
        elif predicate == "locationIdentifier":
            location_info.append(f"{subject}'s location identifier is {object_val}")
        elif predicate == "elevationAboveTheSeaLevelInFeet":
            location_info.append(f"{subject} is {object_val} feet above sea level")
        elif predicate == "elevationAboveTheSeaLevelInMetres":
            location_info.append(f"{subject} is {object_val} meters above sea level")
        elif predicate == "1stRunwayNumber":
            location_info.append(f"{subject}'s first runway number is {object_val}")
        elif predicate == "2ndRunwaySurfaceType":
            location_info.append(f"{subject}'s second runway surface is {object_val}")
        elif predicate == "3rdRunwaySurfaceType":
            location_info.append(f"{subject}'s third runway surface is {object_val}")
        elif predicate == "runwaySurfaceType":
            location_info.append(f"{subject}'s runway surface is {object_val}")
        else:
            other_info.append(f"{subject} {predicate} {object_val}")

    sentence = ""
    if airport_name:
        sentence += f"{airport_name}. "

    if country_info:
        sentence += f"It is located in {country_info} and "

    if location_info:
        sentence += ", ".join(location_info) + ". "

    if other_info:
        sentence += ", ".join(other_info) + "."

    return sentence

