from dataclasses import dataclass

@dataclass
class Triple:
    subject: str
    predicate: str
    object: str

# EVOLVE-BLOCK-START

def predict(triples: list[Triple]) -> str:
    """
    Generates a sentence from a list of triples related to airports.
    """
    airport = None
    location_info = []
    runway_info = []
    other_info = []

    for triple in triples:
        subject = triple.subject
        predicate = triple.predicate
        object_val = triple.object

        if predicate == "cityServed":
            location_info.append(f"{subject} serves {object_val}")
        elif predicate == "country":
            location_info.append(f"{subject} is in {object_val}")
        elif predicate == "elevationAboveTheSeaLevel":
            location_info.append(f"{subject} is {object_val}m above sea level")
        elif predicate == "elevationAboveTheSeaLevelInFeet":
            location_info.append(f"{subject} is {object_val}ft above sea level")
        elif predicate == "runwayLength":
            runway_info.append(f"{subject} has a runway length of {object_val}m")
        elif predicate == "iataLocationIdentifier":
            location_info.append(f"{subject}'s IATA code is {object_val}")
        elif predicate == "location":
            location_info.append(f"{subject} is located in {object_val}")
        elif predicate == "operatingOrganisation":
            other_info.append(f"{subject} is operated by {object_val}")
        elif predicate == "hubAirport":
            other_info.append(f"{subject} is a hub for {object_val}")
        elif predicate == "headquarter":
            other_info.append(f"The headquarters of {subject} is in {object_val}")
        elif predicate == "nativeName":
            airport = object_val
        elif predicate == "runwayName":
            runway_info.append(f"{subject} has runway {object_val}")
        elif predicate == "runwaySurfaceType":
            runway_info.append(f"{subject}'s runway surface is {object_val}")
        elif predicate == "isPartOf":
            location_info.append(f"{subject} is part of {object_val}")
        elif predicate == "1stRunwayLengthFeet":
            runway_info.append(f"{subject}'s first runway is {object_val}ft long")
        elif predicate == "1stRunwaySurfaceType":
            runway_info.append(f"{subject}'s first runway is made of {object_val}")
        elif predicate == "1stRunwayNumber":
            runway_info.append(f"{subject}'s first runway is number {object_val}")
        elif predicate == "2ndRunwaySurfaceType":
            runway_info.append(f"{subject}'s second runway is made of {object_val}")
        elif predicate == "3rdRunwaySurfaceType":
            runway_info.append(f"{subject}'s third runway is made of {object_val}")
        elif predicate == "4thRunwaySurfaceType":
            runway_info.append(f"{subject}'s fourth runway is made of {object_val}")
        elif predicate == "5thRunwaySurfaceType":
            runway_info.append(f"{subject}'s fifth runway is made of {object_val}")
        else:
            other_info.append(f"{subject} {predicate} {object_val}")

    sentence_parts = []
    if airport:
        sentence_parts.append(airport)
    sentence_parts.extend(location_info)
    sentence_parts.extend(runway_info)
    sentence_parts.extend(other_info)

    if sentence_parts:
        sentence = ", ".join(sentence_parts) + "."
        # Attempt to combine related information for better flow
        sentence = sentence.replace(", " + "is located in", " in")
        sentence = sentence.replace(", " + "serves", " serving")
        sentence = sentence.replace(", " + "is", " and is")

        return sentence
    else:
        return ""

