from dataclasses import dataclass

@dataclass
class Triple:
    subject: str
    predicate: str
    object: str

# EVOLVE-BLOCK-START

def predict(triples: list[Triple]) -> str:
    """
    Generates a sentence from a list of triples related to airports.
    """
    airport = None
    location_info = []
    country_info = []
    elevation_info = []
    runway_info = []
    iata_info = []
    operator_info = []
    hub_info = []
    headquarter_info = []
    runway_name_info = []

    for triple in triples:
        if triple.predicate == "cityServed":
            location_info.append(f"serves the city of {triple.object}")
        elif triple.predicate == "country":
            country_info.append(f"is located in {triple.object}")
        elif triple.predicate == "elevationAboveTheSeaLevel":
            elevation_info.append(f"has an elevation of {triple.object} above sea level")
        elif triple.predicate == "runwayLength":
            runway_info.append(f"has a runway length of {triple.object} meters")
        elif triple.predicate == "iataLocationIdentifier":
            iata_info.append(f"has an IATA identifier of {triple.object}")
        elif triple.predicate == "location":
            location_info.append(f"is located in {triple.object}")
        elif triple.predicate == "nativeName":
            airport = triple.object
        elif triple.predicate == "operatingOrganisation":
            operator_info.append(f"is operated by {triple.object}")
        elif triple.predicate == "hubAirport":
            hub_info.append(f"is a hub for {triple.object}")
        elif triple.predicate == "headquarter":
            headquarter_info.append(f"has headquarters in {triple.object}")
        elif triple.predicate == "runwayName":
            runway_name_info.append(f"with runway {triple.object}")
        else:
            pass

    sentence = ""
    if airport:
        sentence += f"{airport} "

    if location_info:
        sentence += ", ".join(location_info) + " "
    if country_info:
        sentence += ", ".join(country_info) + " "
    if elevation_info:
        sentence += ", ".join(elevation_info) + " "
    if runway_info:
        sentence += ", ".join(runway_info) + " "
    if iata_info:
        sentence += ", ".join(iata_info) + " "
    if operator_info:
        sentence += ", ".join(operator_info) + " "
    if hub_info:
        sentence += ", ".join(hub_info) + " "
    if headquarter_info:
        sentence += ", ".join(headquarter_info) + " "
    if runway_name_info:
        sentence += ", ".join(runway_name_info) + " "

    return sentence.strip() + "."

