from dataclasses import dataclass

@dataclass
class Triple:
    subject: str
    predicate: str
    object: str

# EVOLVE-BLOCK-START

def predict(triples: list[Triple]) -> str:
    """
    Generates a sentence from a list of triples related to airports.
    """
    sentence = ""
    airport_info = []
    location_info = []
    country_info = None
    runway_info = []
    organization_info = []
    other_info = []

    for triple in triples:
        if triple.predicate == "cityServed":
            location_info.append(f"{triple.subject} serves {triple.object}")
        elif triple.predicate == "country":
            country_info = triple.object
        elif triple.predicate == "elevationAboveTheSeaLevel":
            other_info.append(f"{triple.subject} is at an elevation of {triple.object} meters")
        elif triple.predicate == "runwayLength":
            runway_info.append(f"{triple.subject} has a runway length of {triple.object} meters")
        elif triple.predicate == "iataLocationIdentifier":
            airport_info.append(f"{triple.subject} (IATA: {triple.object})")
        elif triple.predicate == "location":
            location_info.append(f"{triple.subject} is located in {triple.object}")
        elif triple.predicate == "nativeName":
            airport_info.append(f"{triple.subject}, also known as {triple.object}")
        elif triple.predicate == "operatingOrganisation":
            organization_info.append(f"{triple.subject} is operated by {triple.object}")
        elif triple.predicate == "hubAirport":
            organization_info.append(f"{triple.subject} is a hub for {triple.object}")
        elif triple.predicate == "headquarter":
            organization_info.append(f"{triple.subject}'s headquarters are in {triple.object}")
        elif triple.predicate == "runwayName":
            runway_info.append(f"{triple.subject} has a runway named {triple.object}")
        elif triple.predicate == "capital":
            other_info.append(f"{triple.subject} is the capital of {triple.object}")
        elif triple.predicate == "isPartOf":
            other_info.append(f"{triple.subject} is part of {triple.object}")
        else:
            other_info.append(f"{triple.subject} {triple.predicate} {triple.object}")

    parts = []
    if airport_info:
        parts.extend(airport_info)
    if country_info:
        parts.append(f"located in {country_info}")
    if location_info:
        parts.extend(location_info)
    if runway_info:
        parts.extend(runway_info)
    if organization_info:
        parts.extend(organization_info)
    if other_info:
        parts.extend(other_info)

    sentence = ", ".join(parts) + "."

    return sentence.strip().replace("..", ".")

# EVOLVE-BLOCK-END
