from dataclasses import dataclass

@dataclass
class Triple:
    subject: str
    predicate: str
    object: str

# EVOLVE-BLOCK-START

def predict(triples: list[Triple]) -> str:
    """
    Generates a sentence from a list of triples related to airports.
    """
    airport = None
    city = None
    country = None
    elevation = None
    runway_length = None
    iata = None
    location = None
    operator = None
    runway_name = None

    sentence_parts = []

    for triple in triples:
        if triple.predicate == "cityServed":
            city = triple.object
        elif triple.predicate == "country":
            country = triple.object
        elif triple.predicate == "elevationAboveTheSeaLevel":
            elevation = triple.object
        elif triple.predicate == "runwayLength":
            runway_length = triple.object
        elif triple.predicate == "iataLocationIdentifier":
            iata = triple.object
        elif triple.predicate == "location":
            location = triple.object
        elif triple.predicate == "nativeName":
            airport = triple.subject
        elif triple.predicate == "operatingOrganisation":
            operator = triple.object
        elif triple.predicate == "runwayName":
            runway_name = triple.object
        else:
            pass

    if airport:
        sentence_parts.append(airport)
    if operator:
        sentence_parts.append(f"is operated by {operator}")
    if location:
        sentence_parts.append(f"is located in {location}")
    if country:
        sentence_parts.append(f"which is in {country}")
    if elevation:
        sentence_parts.append(f"with an elevation of {elevation}")
    if runway_length:
        sentence_parts.append(f"and has a runway length of {runway_length} meters")
    if iata:
        sentence_parts.append(f"with IATA code {iata}")
    if runway_name:
        sentence_parts.append(f"with runway {runway_name}")
    if city:
        sentence_parts.append(f"serves the city of {city}")

    sentence = ", ".join(sentence_parts)
    return sentence + "." if sentence else "."

