from dataclasses import dataclass

@dataclass
class Triple:
    subject: str
    predicate: str
    object: str

# EVOLVE-BLOCK-START

def predict(triples: list[Triple]) -> str:
    """
    Generates a sentence from a list of triples related to airports.
    """
    airport = None
    city = None
    country = None
    elevation = None
    runway_length = None
    iata = None
    location = None
    operating_organisation = None
    hub_airport = None
    headquarter = None
    sentence_parts = []

    for triple in triples:
        subject = triple.subject
        predicate = triple.predicate
        object_val = triple.object

        if predicate == "cityServed":
            sentence_parts.append(f"{subject} serves the city of {object_val}")
        elif predicate == "country":
            sentence_parts.append(f"{subject} is located in {object_val}")
        elif predicate == "elevationAboveTheSeaLevel":
            sentence_parts.append(f"{subject} has an elevation of {object_val}")
        elif predicate == "runwayLength":
            sentence_parts.append(f"{subject} has a runway length of {object_val}")
        elif predicate == "iataLocationIdentifier":
            sentence_parts.append(f"{subject} has an IATA code of {object_val}")
        elif predicate == "location":
            sentence_parts.append(f"{subject} is located in {object_val}")
        elif predicate == "nativeName":
            airport = object_val
        elif predicate == "operatingOrganisation":
            sentence_parts.append(f"{subject} is operated by {object_val}")
        elif predicate == "hubAirport":
            sentence_parts.append(f"{subject} is a hub for {object_val}")
        elif predicate == "headquarter":
            sentence_parts.append(f"{subject} has headquarters in {object_val}")
        else:
            pass  # Ignore unknown predicates

    if airport:
        sentence = f"{airport} "
    else:
        sentence = ""

    sentence += ", ".join(sentence_parts)
    return sentence + "."

