from dataclasses import dataclass

@dataclass
class Triple:
    subject: str
    predicate: str
    object: str

# EVOLVE-BLOCK-START

def predict(triples: list[Triple]) -> str:
    """
    Generates a sentence from a list of triples related to airports.
    """
    sentence = ""
    airport = None
    city = None
    country = None
    runway = None
    icao = None
    iata = None

    for triple in triples:
        if triple.predicate == "cityServed":
            city = triple.object
        elif triple.predicate == "country":
            country = triple.object
        elif triple.predicate == "nativeName":
            airport = triple.subject
        elif triple.predicate == "runwayName":
            runway = triple.object
        elif triple.predicate == "icaoLocationIdentifier":
            icao = triple.object
        elif triple.predicate == "iataLocationIdentifier":
            iata = triple.object
        else:
            continue

    parts = []
    if airport:
        parts.append(airport)
    if city:
        parts.append(f"serves the city of {city}")
    if country:
        parts.append(f"which is located in {country}")
    if runway:
        parts.append(f"with runway {runway}")
    if icao:
        parts.append(f"and ICAO identifier {icao}")
    if iata:
        parts.append(f"and IATA identifier {iata}")

    sentence = " ".join(parts)
    if sentence:
        sentence += "."
    return sentence.strip()

