from dataclasses import dataclass

@dataclass
class Triple:
    subject: str
    predicate: str
    object: str

# EVOLVE-BLOCK-START

def predict(triples: list[Triple]) -> str:
    """
    Generates a sentence from a list of triples related to airports.
    """
    airport = None
    city = None
    country = None
    elevation = None
    runway_length = None
    iata = None
    location = None
    operating_organisation = None
    hub_airport = None
    headquarter = None
    runway_name = None

    for triple in triples:
        if triple.predicate == "cityServed":
            city = triple.object
        elif triple.predicate == "country":
            country = triple.object
        elif triple.predicate == "elevationAboveTheSeaLevel":
            elevation = triple.object
        elif triple.predicate == "runwayLength":
            runway_length = triple.object
        elif triple.predicate == "iataLocationIdentifier":
            iata = triple.object
        elif triple.predicate == "location":
            location = triple.object
        elif triple.predicate == "nativeName":
            airport = triple.object
        elif triple.predicate == "operatingOrganisation":
            operating_organisation = triple.object
        elif triple.predicate == "hubAirport":
            hub_airport = triple.object
        elif triple.predicate == "headquarter":
            headquarter = triple.object
        elif triple.predicate == "runwayName":
            runway_name = triple.object
        else:
            airport = triple.subject if airport is None else airport

    sentence = ""
    if airport:
        sentence += f"{airport} "
    if city:
        sentence += f"serves the city of {city} "
    if country:
        sentence += f"which is in {country} "
    if elevation:
        sentence += f"with an elevation of {elevation} "
    if runway_length:
        sentence += f"and has a runway length of {runway_length} "
    if iata:
        sentence += f"with IATA code {iata} "
    if location:
        sentence += f"located in {location} "
    if operating_organisation:
        sentence += f"and is operated by {operating_organisation} "
    if hub_airport:
        sentence += f"and is a hub for {hub_airport} "
    if headquarter:
        sentence += f"with headquarters in {headquarter} "
    if runway_name:
        sentence += f"with runway {runway_name} "

    return sentence.strip() + "."

