from dataclasses import dataclass

@dataclass
class Triple:
    subject: str
    predicate: str
    object: str

# EVOLVE-BLOCK-START

def predict(triples: list[Triple]) -> str:
    """
    Generates a sentence from a list of triples related to airports.
    """
    airport = None
    location = None
    country = None
    operator = None
    elevation = None
    runway_length = None
    iata = None
    other_info = []

    for triple in triples:
        if triple.predicate == "cityServed":
            location = triple.object
        elif triple.predicate == "country":
            country = triple.object
        elif triple.predicate == "elevationAboveTheSeaLevel":
            elevation = triple.object
        elif triple.predicate == "runwayLength":
            runway_length = triple.object
        elif triple.predicate == "iataLocationIdentifier":
            iata = triple.object
        elif triple.predicate == "location":
            location = triple.object  # Overwrite if cityServed also exists
        elif triple.predicate == "nativeName":
            airport = triple.object
        elif triple.predicate == "operatingOrganisation":
            operator = triple.object
        else:
            other_info.append(f"{triple.subject} {triple.predicate} {triple.object}")

    sentence = ""
    if airport:
        sentence += f"{airport}"
        if location:
            sentence += f" serves the city of {location}"
            if country:
                sentence += f", which is in {country}"
        elif country:
            sentence += f" is located in {country}"

        if operator:
            sentence += f" and is operated by {operator}"
        if elevation:
            sentence += f", with an elevation of {elevation} above sea level"
        if runway_length:
            sentence += f", featuring a runway length of {runway_length} meters"
        if iata:
            sentence += f" and has an IATA identifier of {iata}"
    elif location:
        sentence += f"{location}"
        if country:
            sentence += f", which is in {country}"

    if other_info:
        sentence += ". Additionally, " + " and ".join(other_info) + "."
    else:
        sentence += "."

    return sentence.strip().replace(". .", ".") + "."

