from dataclasses import dataclass

@dataclass
class Triple:
    subject: str
    predicate: str
    object: str

# EVOLVE-BLOCK-START

def predict(triples: list[Triple]) -> str:
    """
    Generates a sentence from a list of triples related to airports.
    """
    sentence = ""
    subject = None
    clauses = []

    for triple in triples:
        if triple.predicate == "cityServed":
            clauses.append(f"{triple.subject} serves the city of {triple.object}")
        elif triple.predicate == "country":
            clauses.append(f"{triple.subject} is in {triple.object}")
        elif triple.predicate == "elevationAboveTheSeaLevel":
            clauses.append(f"{triple.subject} has an elevation of {triple.object} above sea level")
        elif triple.predicate == "runwayLength":
            clauses.append(f"{triple.subject} has a runway length of {triple.object} meters")
        elif triple.predicate == "iataLocationIdentifier":
            clauses.append(f"{triple.subject} has an IATA identifier of {triple.object}")
        elif triple.predicate == "location":
            clauses.append(f"{triple.subject} is located in {triple.object}")
        elif triple.predicate == "operatingOrganisation":
            clauses.append(f"{triple.subject} is operated by {triple.object}")
        elif triple.predicate == "hubAirport":
            clauses.append(f"{triple.subject} is a hub for {triple.object}")
        elif triple.predicate == "headquarter":
            clauses.append(f"{triple.subject} has its headquarters in {triple.object}")
        elif triple.predicate == "nativeName":
            subject = triple.object
        elif triple.predicate == "runwayName":
            clauses.append(f"{triple.subject} has a runway named {triple.object}")
        elif triple.predicate == "capital":
            clauses.append(f"{triple.subject} has {triple.object} as its capital")
        elif triple.predicate == "isPartOf":
            clauses.append(f"{triple.subject} is part of {triple.object}")
        elif triple.predicate == "1stRunwayLengthFeet":
            clauses.append(f"{triple.subject} has a first runway length of {triple.object} feet")
        elif triple.predicate == "1stRunwaySurfaceType":
            clauses.append(f"{triple.subject} has a first runway surface type of {triple.object}")
        elif triple.predicate == "3rdRunwayLengthFeet":
            clauses.append(f"{triple.subject} has a third runway length of {triple.object} feet")
        elif triple.predicate == "icaoLocationIdentifier":
            clauses.append(f"{triple.subject} has an ICAO identifier of {triple.object}")
        elif triple.predicate == "locationIdentifier":
            clauses.append(f"{triple.subject} has a location identifier of {triple.object}")
        elif triple.predicate == "elevationAboveTheSeaLevelInFeet":
            clauses.append(f"{triple.subject} has an elevation of {triple.object} feet above sea level")
        elif triple.predicate == "elevationAboveTheSeaLevelInMetres":
            clauses.append(f"{triple.subject} has an elevation of {triple.object} meters above sea level")
        elif triple.predicate == "officialLanguage":
            clauses.append(f"{triple.subject} has {triple.object} as its official language")
        elif triple.predicate == "largestCity":
            clauses.append(f"{triple.subject} has {triple.object} as its largest city")
        elif triple.predicate == "countySeat":
            clauses.append(f"{triple.subject} has {triple.object} as its county seat")
        elif triple.predicate == "leaderTitle":
            clauses.append(f"{triple.subject} is governed by {triple.object}")
        elif triple.predicate == "foundingYear":
            clauses.append(f"{triple.subject} was founded in {triple.object}")
        elif triple.predicate == "ceremonialCounty":
            clauses.append(f"{triple.subject} is in the ceremonial county of {triple.object}")
        else:
            pass

    if subject:
        sentence += subject + " "

    if clauses:
        sentence += ", ".join(clauses)

    return sentence + "."

