from dataclasses import dataclass

@dataclass
class Triple:
    subject: str
    predicate: str
    object: str

# EVOLVE-BLOCK-START

def predict(triples: list[Triple]) -> str:
    """
    Generates a sentence from a list of triples related to airports.
    """
    airport = None
    country = None
    city = None
    elevation = None
    runway_details = []
    other_details = []

    for triple in triples:
        subject = triple.subject
        predicate = triple.predicate
        object_val = triple.object

        if predicate == "cityServed":
            city = object_val
        elif predicate == "country":
            country = object_val
        elif predicate == "elevationAboveTheSeaLevel":
            elevation = f"{object_val} meters"
        elif predicate == "elevationAboveTheSeaLevelInFeet":
            elevation = f"{object_val} feet"
        elif predicate == "runwayLength":
            runway_details.append(f"a runway length of {object_val} meters")
        elif predicate == "iataLocationIdentifier":
            other_details.append(f"with IATA code {object_val}")
        elif predicate == "location":
            city = object_val
        elif predicate == "operatingOrganisation":
            other_details.append(f"operated by {object_val}")
        elif predicate == "hubAirport":
            other_details.append(f"a hub airport for {object_val}")
        elif predicate == "headquarter":
            other_details.append(f"headquartered in {object_val}")
        elif predicate == "nativeName":
            airport = object_val
        elif predicate == "runwayName":
            runway_details.append(f"a runway named {object_val}")
        elif predicate == "runwaySurfaceType":
            runway_details.append(f"a runway surface of {object_val}")
        elif predicate == "isPartOf":
            other_details.append(f"which is part of {object_val}")
        elif predicate == "1stRunwayLengthFeet":
            runway_details.append(f"a first runway length of {object_val} feet")
        elif predicate == "1stRunwaySurfaceType":
            runway_details.append(f"a first runway surface of {object_val}")
        elif predicate == "1stRunwayNumber":
            runway_details.append(f"a first runway number {object_val}")
        else:
            other_details.append(f"{predicate}: {object_val}")

    sentence = ""
    if airport:
        sentence += f"{airport}"

    if country:
        sentence += f" in {country}"
    if city:
        sentence += f" serving {city}"
    if elevation:
        sentence += f" at an elevation of {elevation}"

    if runway_details:
        sentence += f" with {', '.join(runway_details)}"
    if other_details:
        sentence += f", {', '.join(other_details)}"

    if sentence:
        sentence += "."

    return sentence

