from dataclasses import dataclass

@dataclass
class Triple:
    subject: str
    predicate: str
    object: str

# EVOLVE-BLOCK-START

def predict(triples: list[Triple]) -> str:
    """Generates a sentence from a list of airport-related triples."""

    airport_name = None
    city = None
    country = None
    elevation = None
    runway_length = None
    iata_code = None
    location_info = None
    operating_org = None
    hub = None
    headquarters = None

    for triple in triples:
        if triple.predicate == "cityServed":
            city = triple.object
        elif triple.predicate == "country":
            country = triple.object
        elif triple.predicate == "elevationAboveTheSeaLevel":
            elevation = triple.object
        elif triple.predicate == "runwayLength":
            runway_length = triple.object
        elif triple.predicate == "iataLocationIdentifier":
            iata_code = triple.object
        elif triple.predicate == "location":
            location_info = triple.object
        elif triple.predicate == "nativeName":
            airport_name = triple.object
        elif triple.predicate == "operatingOrganisation":
            operating_org = triple.object
        elif triple.predicate == "hubAirport":
            hub = triple.object
        elif triple.predicate == "headquarter":
            headquarters = triple.object
        elif triple.predicate == "locationIdentifier":
            iata_code = triple.object # Use locationIdentifier as a fallback for IATA
        else:
            airport_name = triple.subject if airport_name is None else airport_name

    sentence = ""
    if airport_name:
        sentence += f"{airport_name}"
        if operating_org:
            sentence += f" is operated by {operating_org}"
        if location_info:
            sentence += f" is located in {location_info}"
    if city:
        sentence += f" serves the city of {city}"
    if country:
        sentence += f", which is in {country}"
    if elevation:
        sentence += f", with an elevation of {elevation}"
    if runway_length:
        sentence += f" and has a runway length of {runway_length}"
    if iata_code:
        sentence += f" (IATA code: {iata_code})"
    if hub:
        sentence += f" and serves as a hub for {hub}"
    if headquarters:
        sentence += f", with headquarters in {headquarters}"

    return sentence + "."

