from dataclasses import dataclass

@dataclass
class Triple:
    subject: str
    predicate: str
    object: str

# EVOLVE-BLOCK-START

def predict(triples: list[Triple]) -> str:
    """
    Generates a sentence from a list of triples related to airports.
    """
    airport = None
    city = None
    country = None
    elevation = None
    runway_length = None
    iata = None
    location = None
    operating_organisation = None
    runway_surface_type = None
    info = []

    for triple in triples:
        if triple.predicate == "cityServed":
            info.append(f"serves the city of {triple.object}")
        elif triple.predicate == "country":
            info.append(f"is located in {triple.object}")
        elif triple.predicate == "elevationAboveTheSeaLevel":
            info.append(f"has an elevation of {triple.object}")
        elif triple.predicate == "runwayLength":
            info.append(f"has a runway length of {triple.object}")
        elif triple.predicate == "iataLocationIdentifier":
            info.append(f"has an IATA code of {triple.object}")
        elif triple.predicate == "location":
            info.append(f"is located in {triple.object}")
        elif triple.predicate == "nativeName":
            airport = triple.object
        elif triple.predicate == "operatingOrganisation":
            info.append(f"is operated by {triple.object}")
        elif triple.predicate == "runwaySurfaceType":
            info.append(f"has a runway surface of {triple.object}")
        else:
            pass

    sentence = ""
    if airport:
        sentence += f"{airport} "
    if info:
        sentence += ", ".join(info)

    return sentence.strip() + "."

