from dataclasses import dataclass

@dataclass
class Triple:
    subject: str
    predicate: str
    object: str

# EVOLVE-BLOCK-START

def predict(triples: list[Triple]) -> str:
    """
    Generates a sentence from a list of triples related to airports.
    """
    sentence_parts = []

    for triple in triples:
        subject = triple.subject
        predicate = triple.predicate
        object_val = triple.object

        if predicate == "cityServed":
            sentence_parts.append(f"{subject} serves the city of {object_val}")
        elif predicate == "country":
            sentence_parts.append(f"{subject} is located in {object_val}")
        elif predicate == "elevationAboveTheSeaLevel":
            sentence_parts.append(f"{subject} is {object_val} meters above sea level")
        elif predicate == "elevationAboveTheSeaLevelInFeet":
            sentence_parts.append(f"{subject} is {object_val} feet above sea level")
        elif predicate == "runwayLength":
            sentence_parts.append(f"{subject} has a runway length of {object_val} meters")
        elif predicate == "iataLocationIdentifier":
            sentence_parts.append(f"{subject} has IATA code {object_val}")
        elif predicate == "location":
            sentence_parts.append(f"{subject} is located in {object_val}")
        elif predicate == "operatingOrganisation":
            sentence_parts.append(f"{subject} is operated by {object_val}")
        elif predicate == "hubAirport":
            sentence_parts.append(f"{subject} is a hub airport for {object_val}")
        elif predicate == "headquarter":
            sentence_parts.append(f"The headquarters of {subject} is in {object_val}")
        elif predicate == "nativeName":
            sentence_parts.append(f"{subject} is also known as {object_val}")
        elif predicate == "runwayName":
            sentence_parts.append(f"{subject} has a runway named {object_val}")
        elif predicate == "runwaySurfaceType":
            sentence_parts.append(f"The runway surface of {subject} is {object_val}")
        elif predicate == "isPartOf":
            sentence_parts.append(f"{subject} is part of {object_val}")
        elif predicate == "1stRunwayLengthFeet":
            sentence_parts.append(f"The first runway of {subject} is {object_val} feet long")
        elif predicate == "1stRunwaySurfaceType":
            sentence_parts.append(f"The first runway of {subject} is made of {object_val}")
        elif predicate == "1stRunwayNumber":
            sentence_parts.append(f"The first runway of {subject} is number {object_val}")
        elif predicate == "2ndRunwaySurfaceType":
            sentence_parts.append(f"The second runway of {subject} is made of {object_val}")
        elif predicate == "3rdRunwaySurfaceType":
            sentence_parts.append(f"The third runway of {subject} is made of {object_val}")
        elif predicate == "4thRunwaySurfaceType":
            sentence_parts.append(f"The fourth runway of {subject} is made of {object_val}")
        elif predicate == "5thRunwaySurfaceType":
            sentence_parts.append(f"The fifth runway of {subject} is made of {object_val}")
        else:
            sentence_parts.append(f"{subject} {predicate} {object_val}")

    if sentence_parts:
        sentence = ", ".join(sentence_parts) + "."
        sentence = sentence[0].upper() + sentence[1:]
        return sentence
    else:
        return ""

