from dataclasses import dataclass

@dataclass
class Triple:
    subject: str
    predicate: str
    object: str

# EVOLVE-BLOCK-START

def predict(triples: list[Triple]) -> str:
    """
    Generates a sentence from a list of triples related to airports.
    """
    sentence = ""
    airport = None
    location_info = []

    for triple in triples:
        subject = triple.subject
        predicate = triple.predicate
        object_val = triple.object

        if predicate == "cityServed":
            location_info.append(f"{subject} serves the city of {object_val}")
        elif predicate == "country":
            location_info.append(f"{subject} is located in {object_val}")
        elif predicate == "elevationAboveTheSeaLevel":
            location_info.append(f"{subject} has an elevation of {object_val} above sea level")
        elif predicate == "runwayLength":
            location_info.append(f"{subject} has a runway length of {object_val} meters")
        elif predicate == "iataLocationIdentifier":
            location_info.append(f"{subject} has an IATA code of {object_val}")
        elif predicate == "location":
            location_info.append(f"{subject} is located in {object_val}")
        elif predicate == "operatingOrganisation":
            location_info.append(f"{subject} is operated by {object_val}")
        elif predicate == "hubAirport":
            location_info.append(f"{subject} is a hub for {object_val}")
        elif predicate == "headquarter":
            location_info.append(f"{subject} has headquarters in {object_val}")
        elif predicate == "nativeName":
            airport = object_val
        elif predicate == "runwayName":
            location_info.append(f"{subject} has a runway named {object_val}")
        elif predicate == "1stRunwayLengthFeet":
            location_info.append(f"{subject} has a first runway length of {object_val} feet")
        elif predicate == "1stRunwaySurfaceType":
            location_info.append(f"{subject} has a first runway surface type of {object_val}")
        elif predicate == "3rdRunwayLengthFeet":
            location_info.append(f"{subject} has a third runway length of {object_val} feet")
        elif predicate == "icaoLocationIdentifier":
            location_info.append(f"{subject} has an ICAO identifier of {object_val}")
        elif predicate == "locationIdentifier":
            location_info.append(f"{subject} has a location identifier of {object_val}")
        elif predicate == "elevationAboveTheSeaLevelInFeet":
            location_info.append(f"{subject} has an elevation of {object_val} feet above sea level")
        elif predicate == "elevationAboveTheSeaLevelInMetres":
            location_info.append(f"{subject} has an elevation of {object_val} meters above sea level")
        elif predicate == "1stRunwayNumber":
            location_info.append(f"{subject} has a first runway number of {object_val}")
        elif predicate == "2ndRunwaySurfaceType":
            location_info.append(f"{subject} has a second runway surface type of {object_val}")
        elif predicate == "3rdRunwaySurfaceType":
            location_info.append(f"{subject} has a third runway surface type of {object_val}")
        elif predicate == "runwaySurfaceType":
            location_info.append(f"{subject} has a runway surface type of {object_val}")
        else:
            pass

    if airport:
        sentence += f"{airport} "

    if location_info:
        sentence += ", ".join(location_info)

    return sentence + "."

