from dataclasses import dataclass

@dataclass
class Triple:
    subject: str
    predicate: str
    object: str

# EVOLVE-BLOCK-START

def predict(triples: list[Triple]) -> str:
    airport = None
    city = None
    country = None
    elevation = None
    runway_length = None
    iata = None
    location = None
    operating_organization = None
    runway_surface_type = None
    leader = None
    capital = None
    hub_airport = None

    for triple in triples:
        if triple.predicate == "cityServed":
            city = triple.object
        elif triple.predicate == "country":
            country = triple.object
        elif triple.predicate == "elevationAboveTheSeaLevel":
            elevation = triple.object
        elif triple.predicate == "runwayLength":
            runway_length = triple.object
        elif triple.predicate == "iataLocationIdentifier":
            iata = triple.object
        elif triple.predicate == "location":
            location = triple.object
        elif triple.predicate == "operatingOrganisation":
            operating_organization = triple.object
        elif triple.predicate == "runwaySurfaceType":
            runway_surface_type = triple.object
        elif triple.predicate == "leader":
            leader = triple.object
        elif triple.predicate == "capital":
            capital = triple.object
        elif triple.predicate == "hubAirport":
            hub_airport = triple.object
        elif triple.predicate == "nativeName":
            airport = triple.object
        else:
            pass

    sentence = ""
    if airport:
        sentence += f"{airport}"
        if operating_organization:
            sentence += f" is operated by {operating_organization}"
        if hub_airport:
            sentence += f" and is a hub for {hub_airport}"

    if city:
        sentence += f" serves the city of {city}"
    if country:
        sentence += f", which is in {country}"
    if capital:
        sentence += f", the capital is {capital}"
    if leader:
        sentence += f" and its leader is {leader}"

    if elevation:
        sentence += f" with an elevation of {elevation}"
    if runway_length:
        sentence += f" and has a runway length of {runway_length}"
    if iata:
        sentence += f" with IATA code {iata}"
    if location:
        sentence += f" located in {location}"
    if runway_surface_type:
        sentence += f" with a runway surface type of {runway_surface_type}"

    return sentence + "."

