from dataclasses import dataclass

@dataclass
class Triple:
    subject: str
    predicate: str
    object: str

# EVOLVE-BLOCK-START

def predict(triples: list[Triple]) -> str:
    """
    Generates a sentence from a list of triples related to airports.
    """
    sentence_parts = []
    airport_name = None

    for triple in triples:
        subject = triple.subject
        predicate = triple.predicate
        object_val = triple.object

        if predicate == "cityServed":
            sentence_parts.append(f"{subject} serves {object_val}")
        elif predicate == "country":
            sentence_parts.append(f"{subject} is located in {object_val}")
        elif predicate == "elevationAboveTheSeaLevel":
            sentence_parts.append(f"{subject} is {object_val} meters above sea level")
        elif predicate == "runwayLength":
            sentence_parts.append(f"{subject} has a runway length of {object_val} meters")
        elif predicate == "iataLocationIdentifier":
            sentence_parts.append(f"{subject}'s IATA code is {object_val}")
        elif predicate == "location":
            sentence_parts.append(f"{subject} is located in {object_val}")
        elif predicate == "operatingOrganisation":
            sentence_parts.append(f"{subject} is operated by {object_val}")
        elif predicate == "hubAirport":
            sentence_parts.append(f"{subject} is a hub for {object_val}")
        elif predicate == "headquarter":
            sentence_parts.append(f"{subject}'s headquarters are in {object_val}")
        elif predicate == "nativeName":
            airport_name = object_val
        elif predicate == "runwayName":
            sentence_parts.append(f"{subject} has a runway named {object_val}")
        elif predicate == "1stRunwayLengthFeet":
            sentence_parts.append(f"{subject}'s first runway is {object_val} feet long")
        elif predicate == "1stRunwaySurfaceType":
            sentence_parts.append(f"{subject}'s first runway surface is {object_val}")
        elif predicate == "3rdRunwayLengthFeet":
            sentence_parts.append(f"{subject}'s third runway is {object_val} feet long")
        elif predicate == "icaoLocationIdentifier":
            sentence_parts.append(f"{subject}'s ICAO identifier is {object_val}")
        elif predicate == "locationIdentifier":
            sentence_parts.append(f"{subject}'s location identifier is {object_val}")
        elif predicate == "elevationAboveTheSeaLevelInFeet":
            sentence_parts.append(f"{subject} is {object_val} feet above sea level")
        elif predicate == "elevationAboveTheSeaLevelInMetres":
            sentence_parts.append(f"{subject} is {object_val} meters above sea level")
        elif predicate == "1stRunwayNumber":
            sentence_parts.append(f"{subject}'s first runway number is {object_val}")
        elif predicate == "2ndRunwaySurfaceType":
            sentence_parts.append(f"{subject}'s second runway surface is {object_val}")
        elif predicate == "3rdRunwaySurfaceType":
            sentence_parts.append(f"{subject}'s third runway surface is {object_val}")
        elif predicate == "runwaySurfaceType":
            sentence_parts.append(f"{subject}'s runway surface is {object_val}")
        else:
            sentence_parts.append(f"{subject} {predicate} {object_val}")

    sentence = ""
    if airport_name:
        sentence += f"{airport_name} "

    sentence += " and ".join(sentence_parts)
    return sentence + "."

