from dataclasses import dataclass

@dataclass
class Triple:
    subject: str
    predicate: str
    object: str

# EVOLVE-BLOCK-START

def predict(triples: list[Triple]) -> str:
    """
    Generates a sentence from a list of triples related to airports.
    """
    sentence = ""
    subject = None
    location_info = []
    runway_info = []
    other_info = []

    for triple in triples:
        pred = triple.predicate
        subj = triple.subject
        obj = triple.object

        if subject is None:
            subject = subj

        if pred == "cityServed":
            location_info.append(f"{subj} serves the city of {obj}")
        elif pred == "country":
            location_info.append(f"{subj} is located in {obj}")
        elif pred == "elevationAboveTheSeaLevel":
            location_info.append(f"{subj} is {obj} meters above sea level")
        elif pred == "elevationAboveTheSeaLevelInFeet":
            location_info.append(f"{subj} is {obj} feet above sea level")
        elif pred == "runwayLength":
            runway_info.append(f"{subj} has a runway length of {obj} meters")
        elif pred == "iataLocationIdentifier":
            location_info.append(f"{subj} has IATA code {obj}")
        elif pred == "location":
            location_info.append(f"{subj} is located in {obj}")
        elif pred == "operatingOrganisation":
            other_info.append(f"{subj} is operated by {obj}")
        elif pred == "hubAirport":
            other_info.append(f"{subj} is a hub airport for {obj}")
        elif pred == "headquarter":
            other_info.append(f"The headquarters of {subj} is in {obj}")
        elif pred == "nativeName":
            sentence += f"{subj}, also known as {obj}. "
        elif pred == "runwayName":
            runway_info.append(f"{subj} has a runway named {obj}")
        elif pred == "runwaySurfaceType":
            runway_info.append(f"The runway surface of {subj} is {obj}")
        elif pred == "isPartOf":
            location_info.append(f"{subj} is part of {obj}")
        elif pred == "1stRunwayLengthFeet":
            runway_info.append(f"The first runway of {subj} is {obj} feet long")
        elif pred == "1stRunwaySurfaceType":
            runway_info.append(f"The first runway of {subj} is made of {obj}")
        elif pred == "1stRunwayNumber":
            runway_info.append(f"The first runway of {subj} is number {obj}")
        elif pred == "2ndRunwaySurfaceType":
            runway_info.append(f"The second runway of {subj} is made of {obj}")
        elif pred == "3rdRunwaySurfaceType":
            runway_info.append(f"The third runway of {subj} is made of {obj}")
        elif pred == "4thRunwaySurfaceType":
            runway_info.append(f"The fourth runway of {subj} is made of {obj}")
        elif pred == "5thRunwaySurfaceType":
            runway_info.append(f"The fifth runway of {subj} is made of {obj}")
        else:
            other_info.append(f"{subj} {pred} {obj}")

    if location_info:
        sentence += ", ".join(location_info) + ". "
    if runway_info:
        sentence += ", ".join(runway_info) + ". "
    if other_info:
        sentence += ", ".join(other_info) + ". "

    return sentence.strip()

