            other_info.append(f"{subject} has location identifier {object_val}")
        elif predicate == "elevationAboveTheSeaLevelInFeet":
            other_info.append(f"{subject} has an elevation of {object_val} feet")
        elif predicate == "leaderParty":
            other_info.append(f"{subject} is led by {object_val}")
