from dataclasses import dataclass

@dataclass
class Triple:
    subject: str
    predicate: str
    object: str

# EVOLVE-BLOCK-START

def predict(triples: list[Triple]) -> str:
    """
    Generates a sentence from a list of triples related to airports.
    """
    sentence = ""
    airport_name = None
    location_details = []

    for triple in triples:
        if triple.predicate == "cityServed":
            location_details.append(f"serves the city of {triple.object}")
        elif triple.predicate == "country":
            location_details.append(f"is located in {triple.object}")
        elif triple.predicate == "elevationAboveTheSeaLevel":
            location_details.append(f"has an elevation of {triple.object} meters")
        elif triple.predicate == "runwayLength":
            location_details.append(f"has a runway length of {triple.object} meters")
        elif triple.predicate == "iataLocationIdentifier":
            location_details.append(f"has IATA identifier {triple.object}")
        elif triple.predicate == "location":
            location_details.append(f"is located in {triple.object}")
        elif triple.predicate == "nativeName":
            airport_name = triple.object
        elif triple.predicate == "operatingOrganisation":
            location_details.append(f"is operated by {triple.object}")
        elif triple.predicate == "hubAirport":
            location_details.append(f"is a hub for {triple.object}")
        elif triple.predicate == "headquarter":
            location_details.append(f"has its headquarters in {triple.object}")
        elif triple.predicate == "runwaySurfaceType":
            location_details.append(f"has a runway surface of {triple.object}")
        elif triple.predicate == "1stRunwayLengthFeet":
            location_details.append(f"has a first runway length of {triple.object} feet")
        elif triple.predicate == "1stRunwaySurfaceType":
            location_details.append(f"has a first runway surface type of {triple.object}")
        elif triple.predicate == "3rdRunwayLengthFeet":
            location_details.append(f"has a third runway length of {triple.object} feet")
        elif triple.predicate == "icaoLocationIdentifier":
            location_details.append(f"has ICAO identifier {triple.object}")
        elif triple.predicate == "locationIdentifier":
            location_details.append(f"has location identifier {triple.object}")
        elif triple.predicate == "elevationAboveTheSeaLevelInFeet":
            location_details.append(f"has an elevation of {triple.object} feet")
        elif triple.predicate == "capital":
            location_details.append(f"has {triple.object} as its capital")
        elif triple.predicate == "largestCity":
            location_details.append(f"has {triple.object} as its largest city")
        elif triple.predicate == "countySeat":
            location_details.append(f"has {triple.object} as its county seat")
        else:
            pass

    if airport_name:
        sentence += f"{airport_name} "

    if location_details:
        sentence += " and ".join(location_details)

    return sentence.strip() + "."

