from dataclasses import dataclass

@dataclass
class Triple:
    subject: str
    predicate: str
    object: str

# EVOLVE-BLOCK-START

def predict(triples: list[Triple]) -> str:
    """
    Generates a sentence from a list of triples related to airports.
    """
    sentence = ""
    airport_name = None
    airport_details = []

    for triple in triples:
        if triple.predicate == "cityServed":
            airport_details.append(f"serves the city of {triple.object}")
        elif triple.predicate == "country":
            airport_details.append(f"is located in {triple.object}")
        elif triple.predicate == "elevationAboveTheSeaLevel":
            airport_details.append(f"has an elevation of {triple.object} above sea level")
        elif triple.predicate == "runwayLength":
            airport_details.append(f"has a runway length of {triple.object} meters")
        elif triple.predicate == "iataLocationIdentifier":
            airport_details.append(f"has an IATA identifier of {triple.object}")
        elif triple.predicate == "location":
            airport_details.append(f"is located in {triple.object}")
        elif triple.predicate == "nativeName":
            airport_name = triple.object
        elif triple.predicate == "operatingOrganisation":
            airport_details.append(f"is operated by {triple.object}")
        elif triple.predicate == "runwayName":
            airport_details.append(f"features runway {triple.object}")
        else:
            airport_details.append(f"{triple.subject} {triple.predicate} {triple.object}")

    if airport_name:
        sentence += f"{airport_name} "

    if airport_details:
        sentence += ", ".join(airport_details) + "."
    else:
        sentence = ""

    return sentence.strip()

