from dataclasses import dataclass

@dataclass
class Triple:
    subject: str
    predicate: str
    object: str

# EVOLVE-BLOCK-START

def predict(triples: list[Triple]) -> str:
    """
    Generates a sentence from a list of triples related to airports.
    """
    airport = None
    location_info = []
    runway_info = []
    other_info = []

    for triple in triples:
        subject = triple.subject
        predicate = triple.predicate
        object_val = triple.object

        if predicate == "cityServed":
            location_info.append(f"{subject} serves the city of {object_val}")
        elif predicate == "country":
            location_info.append(f"{subject} is located in {object_val}")
        elif predicate == "elevationAboveTheSeaLevel":
            location_info.append(f"{subject} has an elevation of {object_val} meters above sea level")
        elif predicate == "elevationAboveTheSeaLevelInFeet":
            location_info.append(f"{subject} has an elevation of {object_val} feet above sea level")
        elif predicate == "runwayLength":
            runway_info.append(f"{subject} has a runway length of {object_val} meters")
        elif predicate == "iataLocationIdentifier":
            location_info.append(f"The IATA code for {subject} is {object_val}")
        elif predicate == "location":
            location_info.append(f"{subject} is located in {object_val}")
        elif predicate == "operatingOrganisation":
            other_info.append(f"{subject} is operated by {object_val}")
        elif predicate == "hubAirport":
            other_info.append(f"{subject} serves as a hub airport for {object_val}")
        elif predicate == "headquarter":
            other_info.append(f"The headquarters of {subject} is located in {object_val}")
        elif predicate == "nativeName":
            airport = object_val
        elif predicate == "runwayName":
            runway_info.append(f"{subject} has a runway named {object_val}")
        elif predicate == "runwaySurfaceType":
            runway_info.append(f"The runway surface at {subject} is {object_val}")
        elif predicate == "isPartOf":
            location_info.append(f"{subject} is part of {object_val}")
        elif predicate == "1stRunwayLengthFeet":
            runway_info.append(f"The first runway at {subject} is {object_val} feet long")
        elif predicate == "1stRunwaySurfaceType":
            runway_info.append(f"The first runway at {subject} is made of {object_val}")
        elif predicate == "1stRunwayNumber":
            runway_info.append(f"The first runway at {subject} is number {object_val}")
        elif predicate == "2ndRunwaySurfaceType":
            runway_info.append(f"The second runway at {subject} is made of {object_val}")
        elif predicate == "3rdRunwaySurfaceType":
            runway_info.append(f"The third runway at {subject} is made of {object_val}")
        elif predicate == "4thRunwaySurfaceType":
            runway_info.append(f"The fourth runway at {subject} is made of {object_val}")
        elif predicate == "5thRunwaySurfaceType":
            runway_info.append(f"The fifth runway at {subject} is made of {object_val}")
        else:
            other_info.append(f"{subject} {predicate} {object_val}")

    sentence_parts = []
    if airport:
        sentence_parts.append(airport)
    sentence_parts.extend(location_info)
    sentence_parts.extend(runway_info)
    sentence_parts.extend(other_info)

    if sentence_parts:
        sentence = " ".join(sentence_parts) + "."
        # Attempt to combine related information for better flow
        sentence = sentence.replace(" is located in ", " in ")
        sentence = sentence.replace(" serves the city of ", " serving ")
        sentence = sentence.replace(" has a runway named ", " with runway ")
        sentence = sentence.replace(" has IATA code ", " IATA code ")
        sentence = sentence.replace(" is operated by ", " operated by ")
        sentence = sentence.replace(" is a hub airport for ", " as a hub for ")
        sentence = sentence.replace(" is part of ", " in ")
        return sentence
    else:
        return ""

