from dataclasses import dataclass

@dataclass
class Triple:
    subject: str
    predicate: str
    object: str

# EVOLVE-BLOCK-START

def predict(triples: list[Triple]) -> str:
    """
    Generates a sentence from a list of triples related to airports.
    """
    sentence = ""
    airport = None
    city = None
    country = None

    for triple in triples:
        if triple.predicate == "cityServed":
            city = triple.object
        elif triple.predicate == "country":
            country = triple.object
        elif triple.predicate == "nativeName":
            airport = triple.subject
        else:
            continue

    if airport and city and country:
        sentence = f"{airport} serves the city of {city} which is located in {country}."
    elif airport and city:
        sentence = f"{airport} serves the city of {city}."
    elif airport and country:
        sentence = f"{airport} is located in {country}."
    elif airport:
        sentence = f"{airport}."
    else:
        sentence = ""

    return sentence.strip() + "."

