from dataclasses import dataclass

@dataclass
class Triple:
    subject: str
    predicate: str
    object: str

# EVOLVE-BLOCK-START

def predict(triples: list[Triple]) -> str:
    """Generates a sentence from a list of airport-related triples."""

    sentence_parts = []
    airport_name = None
    city = None
    country = None
    elevation = None
    runway_length = None
    iata_code = None
    location_info = None
    operating_org = None
    hub = None
    headquarters = None

    for triple in triples:
        subject = triple.subject
        predicate = triple.predicate
        object_val = triple.object

        if predicate == "cityServed":
            sentence_parts.append(f"{subject} serves the city of {object_val}")
        elif predicate == "country":
            country = object_val
        elif predicate == "elevationAboveTheSeaLevel":
            elevation = object_val
        elif predicate == "runwayLength":
            runway_length = object_val
        elif predicate == "iataLocationIdentifier":
            iata_code = object_val
        elif predicate == "location":
            location_info = object_val
        elif predicate == "nativeName":
            airport_name = object_val
        elif predicate == "operatingOrganisation":
            operating_org = object_val
        elif predicate == "hubAirport":
            hub = object_val
        elif predicate == "headquarter":
            headquarters = object_val
        elif predicate == "locationIdentifier":
            iata_code = object_val
        else:
            pass

    if airport_name:
        sentence_parts.append(f"{airport_name}")

    if location_info:
        sentence_parts.append(f"is located in {location_info}")

    if country:
        sentence_parts.append(f"which is in {country}")

    if elevation:
        sentence_parts.append(f"with an elevation of {elevation}")

    if runway_length:
        sentence_parts.append(f"and has a runway length of {runway_length}")

    if iata_code:
        sentence_parts.append(f"(IATA code: {iata_code})")

    if hub:
        sentence_parts.append(f"and serves as a hub for {hub}")

    if headquarters:
        sentence_parts.append(f"with headquarters in {headquarters}")

    if operating_org:
        sentence_parts.append(f"is operated by {operating_org}")

    return ", ".join(sentence_parts) + "."

