from dataclasses import dataclass

@dataclass
class Triple:
    subject: str
    predicate: str
    object: str

# EVOLVE-BLOCK-START

def predict(triples: list[Triple]) -> str:
    """
    Generates a sentence from a list of triples related to airports.
    """
    sentence = ""
    subject = None

    for triple in triples:
        if subject is None:
            subject = triple.subject

        if triple.predicate == "cityServed":
            sentence += f"{subject} serves the city of {triple.object}. "
        elif triple.predicate == "country":
            sentence += f"{subject} is located in {triple.object}. "
        elif triple.predicate == "elevationAboveTheSeaLevel":
            sentence += f"{subject} has an elevation of {triple.object} above sea level. "
        elif triple.predicate == "location":
            sentence += f"{subject} is located in {triple.object}. "
        elif triple.predicate == "operatingOrganisation":
            sentence += f"{subject} is operated by {triple.object}. "
        elif triple.predicate == "runwayLength":
            sentence += f"{subject} has a runway length of {triple.object} meters. "
        elif triple.predicate == "runwayName":
            sentence += f"{subject} has a runway named {triple.object}. "
        elif triple.predicate == "runwaySurfaceType":
            sentence += f"{subject} has a runway surface of {triple.object}. "
        elif triple.predicate == "iataLocationIdentifier":
            sentence += f"{subject}'s IATA code is {triple.object}. "
        elif triple.predicate == "hubAirport":
            sentence += f"{subject} is a hub airport for {triple.object}. "
        elif triple.predicate == "headquarter":
            sentence += f"{subject}'s headquarters are located in {triple.object}. "
        elif triple.predicate == "nativeName":
            sentence += f"{subject} is also known as {triple.object}. "
        elif triple.predicate == "1stRunwayLengthFeet":
            sentence += f"{subject} has a first runway length of {triple.object} feet. "
        elif triple.predicate == "1stRunwaySurfaceType":
            sentence += f"{subject}'s first runway surface is {triple.object}. "
        elif triple.predicate == "1stRunwayNumber":
            sentence += f"{subject} has a first runway numbered {triple.object}. "
        elif triple.predicate == "largestCity":
            sentence += f"{subject}'s largest city is {triple.object}. "
        elif triple.predicate == "countySeat":
            sentence += f"{subject}'s county seat is {triple.object}. "
        elif triple.predicate == "elevationAboveTheSeaLevelInFeet":
            sentence += f"{subject} has an elevation of {triple.object} feet above sea level. "
        elif triple.predicate == "elevationAboveTheSeaLevelInMetres":
            sentence += f"{subject} has an elevation of {triple.object} meters above sea level. "
        else:
            sentence += f"{subject} {triple.predicate} {triple.object}. "

    return sentence.strip() + "."

