from dataclasses import dataclass

@dataclass
class Triple:
    subject: str
    predicate: str
    object: str

# EVOLVE-BLOCK-START

def predict(triples: list[Triple]) -> str:
    # Group triples by subject to create more coherent, compact sentences
    subject_groups = {}
    for triple in triples:
        subject_groups.setdefault(triple.subject, []).append(triple)

    sentences = []
    for subject, subject_triples in subject_groups.items():
        parts = []
        for triple in subject_triples:
            if triple.predicate == "cityServed":
                parts.append(f"serves the city of {triple.object}")
            elif triple.predicate == "country":
                parts.append(f"is in {triple.object}")
            elif triple.predicate == "capital":
                parts.append(f"the capital is {triple.object}")
            elif triple.predicate == "elevationAboveTheSeaLevel":
                parts.append(f"has an elevation of {triple.object} meters above sea level")
            elif triple.predicate == "location":
                parts.append(f"is located in {triple.object}")
            elif triple.predicate == "operatingOrganisation":
                parts.append(f"is operated by {triple.object}")
            elif triple.predicate == "runwayLength":
                parts.append(f"has a runway length of {triple.object} meters")
            elif triple.predicate == "runwayName":
                parts.append(f"has a runway named {triple.object}")
            elif triple.predicate == "isPartOf":
                parts.append(f"is part of {triple.object}")
            elif triple.predicate == "1stRunwayLengthFeet":
                parts.append(f"has a first runway of {triple.object} feet long")
            elif triple.predicate == "1stRunwaySurfaceType":
                parts.append(f"has a first runway surface type of {triple.object}")
            elif triple.predicate == "3rdRunwayLengthFeet":
                parts.append(f"has a third runway of {triple.object} feet long")
            elif triple.predicate == "icaoLocationIdentifier":
                parts.append(f"has ICAO location identifier {triple.object}")
            elif triple.predicate == "locationIdentifier":
                parts.append(f"has location identifier {triple.object}")
            elif triple.predicate == "elevationAboveTheSeaLevelInFeet":
                parts.append(f"has an elevation of {triple.object} feet above sea level")
            elif triple.predicate == "iataLocationIdentifier":
                parts.append(f"has IATA location identifier {triple.object}")
            elif triple.predicate == "nativeName":
                parts.append(f"also known as {triple.object}")
            elif triple.predicate == "leaderParty":
                parts.append(f"is led by the {triple.object}")
            elif triple.predicate == "language":
                parts.append(f"has official language {triple.object}")
            elif triple.predicate == "owner":
                parts.append(f"is owned by {triple.object}")
            elif triple.predicate == "1stRunwayLengthMetre":
                parts.append(f"has a first runway of {triple.object} meters long")
            elif triple.predicate == "4thRunwaySurfaceType":
                parts.append(f"has a fourth runway surface type of {triple.object}")
            elif triple.predicate == "largestCity":
                parts.append(f"has largest city {triple.object}")
            elif triple.predicate == "4thRunwayLengthFeet":
                parts.append(f"has a fourth runway of {triple.object} feet long")
            elif triple.predicate == "elevationAboveTheSeaLevelInMetres":
                parts.append(f"has an elevation of {triple.object} meters above sea level")
            elif triple.predicate == "administrativeArrondissement":
                parts.append(f"is in the {triple.object}")
            elif triple.predicate == "officialLanguage":
                parts.append(f"has official language {triple.object}")
            elif triple.predicate == "regionServed":
                parts.append(f"serves the region of {triple.object}")
            else:
                parts.append(f"{triple.predicate} {triple.object}")

        # Join parts with commas and add subject, with proper conjunctions for better flow
        if parts:
            # Use "and" to connect multiple attributes, and handle special cases like "serves" and "is operated by"
            sentence = f"{subject}"
            for i, part in enumerate(parts):
                if i == 0:
                    sentence += f" {part}"
                else:
                    # Use "and" for last item, "with" for intermediate items to improve readability
                    if i == len(parts) - 1:
                        sentence += f" and {part}"
                    else:
                        sentence += f", {part}"
            sentence += "."
            sentences.append(sentence)

    return " ".join(sentences)

