from dataclasses import dataclass

@dataclass
class Triple:
    subject: str
    predicate: str
    object: str

# EVOLVE-BLOCK-START

def predict(triples: list[Triple]) -> str:
    if not triples:
        return ""

    # Group triples by subject to build richer, connected sentences
    subject_groups = {}
    for triple in triples:
        if triple.subject not in subject_groups:
            subject_groups[triple.subject] = []
        subject_groups[triple.subject].append(triple)

    sentences = []

    for subject, group in subject_groups.items():
        sentence_parts = []

        # Extract and prioritize key information
        city_served = None
        country = None
        elevation = None
        location = None
        operating_org = None
        runway_length = None
        runway_name = None
        owner = None
        icao_id = None
        iata_id = None
        native_name = None
        capital = None
        official_language = None
        region_served = None

        for triple in group:
            if triple.predicate == "cityServed":
                city_served = triple.object
            elif triple.predicate == "country":
                country = triple.object
            elif triple.predicate == "elevationAboveTheSeaLevel":
                elevation = f"{triple.object} meters"
            elif triple.predicate == "elevationAboveTheSeaLevelInFeet":
                elevation = f"{triple.object} feet"
            elif triple.predicate == "location":
                location = triple.object
            elif triple.predicate == "operatingOrganisation":
                operating_org = triple.object
            elif triple.predicate == "runwayLength":
                runway_length = f"{triple.object} meters"
            elif triple.predicate == "runwayName":
                runway_name = triple.object
            elif triple.predicate == "owner":
                owner = triple.object
            elif triple.predicate == "icaoLocationIdentifier":
                icao_id = triple.object
            elif triple.predicate == "iataLocationIdentifier":
                iata_id = triple.object
            elif triple.predicate == "nativeName":
                native_name = triple.object
            elif triple.predicate == "capital":
                capital = triple.object
            elif triple.predicate == "officialLanguage":
                official_language = triple.object
            elif triple.predicate == "regionServed":
                region_served = triple.object

        # Build primary sentence
        if city_served:
            sentence_parts.append(f"{subject} serves the city of {city_served}")
        elif location:
            sentence_parts.append(f"{subject} is located in {location}")
        else:
            sentence_parts.append(f"{subject}")

        # Add context in a natural flow
        if country:
            sentence_parts.append(f", which is in {country}")
        if capital and country:
            sentence_parts.append(f", where the capital is {capital}")
        if official_language:
            sentence_parts.append(f" and the official language is {official_language}")
        if elevation:
            sentence_parts.append(f" with an elevation of {elevation} above sea level")
        if operating_org:
            sentence_parts.append(f", operated by {operating_org}")
        if owner:
            sentence_parts.append(f", owned by {owner}")
        if runway_length:
            sentence_parts.append(f", with a runway length of {runway_length}")
        if runway_name:
            sentence_parts.append(f" and a runway named {runway_name}")
        if icao_id:
            sentence_parts.append(f", with ICAO identifier {icao_id}")
        if iata_id:
            sentence_parts.append(f", and IATA identifier {iata_id}")
        if native_name:
            sentence_parts.append(f", also known as {native_name}")
        if region_served:
            sentence_parts.append(f", serving the region of {region_served}")

        # Finalize sentence
        sentence = "".join(sentence_parts)
        if not sentence.endswith("."):
            sentence += "."
        sentences.append(sentence)

    # Join all sentences
    result = " ".join(sentences)
    if not result.endswith("."):
        result += "."
    return result

# EVOLVE-BLOCK-END
