def predict(triples: list[Triple]) -> str:
    sentence = ""
    airport = None
    location = None
    country = None

    for triple in triples:
        if triple.predicate == "cityServed":
            airport = triple.subject
            location = triple.object
        elif triple.predicate == "country" and location:
            country = triple.object
        elif triple.predicate == "elevationAboveTheSeaLevel":
            elevation = triple.object
        elif triple.predicate == "1stRunwaySurfaceType":
            runway_surface = triple.object
        elif triple.predicate == "location":
            location = triple.object
        else:
            pass # Ignore other triples for now

    if airport and location and country:
        sentence = f"{airport} serves the city of {location}, which is located in {country}."
        if "elevation" in locals():
            sentence += f" It has an elevation of {elevation} meters."
        if "runway_surface" in locals():
            sentence += f" The first runway surface type is {runway_surface}."
    elif airport and location:
        sentence = f"{airport} serves the city of {location}."
    elif airport:
        sentence = f"{airport} is an airport."
    else:
        sentence = ""

    return sentence

