from dataclasses import dataclass

@dataclass
class Triple:
    subject: str
    predicate: str
    object: str

# EVOLVE-BLOCK-START

def predict(triples: list[Triple]) -> str:
    """Generates a coherent, contextually relevant sentence from a list of triples.

    Args:
        triples: A list of Triple objects representing the input data.

    Returns:
        A string representing the generated sentence.
    """

    clauses = []
    for triple in triples:
        predicate = triple.predicate
        object = triple.object
        subject = triple.subject

        if predicate == "cityServed":
            clauses.append(f"{subject} serves the city of {object}")
        elif predicate == "country":
            clauses.append(f"{subject} is located in {object}")
        elif predicate == "capital":
            clauses.append(f"The capital of {subject} is {object}")
        elif predicate == "elevationAboveTheSeaLevel":
            clauses.append(f"{subject} has an elevation of {object} meters")
        elif predicate == "location":
            clauses.append(f"{subject} is located in {object}")
        elif predicate == "operatingOrganisation":
            clauses.append(f"{subject} is operated by {object}")
        elif predicate == "runwayLength":
            clauses.append(f"{subject} has a runway length of {object} meters")
        elif predicate == "runwayName":
            clauses.append(f"{subject} has runway {object}")
        elif predicate == "isPartOf":
            clauses.append(f"{subject} is part of {object}")
        elif predicate == "1stRunwayLengthFeet":
            clauses.append(f"The first runway of {subject} is {object} feet long")
        elif predicate == "1stRunwaySurfaceType":
            clauses.append(f"The first runway surface of {subject} is {object}")
        elif predicate == "3rdRunwayLengthFeet":
            clauses.append(f"The third runway of {subject} is {object} feet long")
        elif predicate == "icaoLocationIdentifier":
            clauses.append(f"The ICAO identifier for {subject} is {object}")
        elif predicate == "locationIdentifier":
            clauses.append(f"The location identifier for {subject} is {object}")
        elif predicate == "elevationAboveTheSeaLevelInFeet":
            clauses.append(f"{subject} has an elevation of {object} feet")
        elif predicate == "iataLocationIdentifier":
            clauses.append(f"The IATA identifier for {subject} is {object}")
        elif predicate == "nativeName":
            clauses.append(f"{subject} is also known as {object}")
        elif predicate == "leaderParty":
            clauses.append(f"{subject} is led by {object}")
        elif predicate == "language":
            clauses.append(f"The official language of {subject} is {object}")
        elif predicate == "owner":
            clauses.append(f"{subject} is owned by {object}")
        elif predicate == "1stRunwayLengthMetre":
            clauses.append(f"The first runway of {subject} is {object} meters long")
        elif predicate == "4thRunwaySurfaceType":
            clauses.append(f"The fourth runway surface of {subject} is {object}")
        elif predicate == "largestCity":
            clauses.append(f"The largest city in {subject} is {object}")
        elif predicate == "4thRunwayLengthFeet":
            clauses.append(f"The fourth runway of {subject} is {object} feet long")
        elif predicate == "elevationAboveTheSeaLevelInMetres":
            clauses.append(f"{subject} has an elevation of {object} meters")
        elif predicate == "administrativeArrondissement":
            clauses.append(f"{subject} is in {object}")
        elif predicate == "officialLanguage":
            clauses.append(f"The official language of {subject} is {object}")
        elif predicate == "regionServed":
            clauses.append(f"{subject} serves the region of {object}")
        else:
            clauses.append(f"{subject} {predicate} {object}")

    # Join the clauses into a sentence and return it
    return ". ".join(clauses) + "."

