from dataclasses import dataclass

@dataclass
class Triple:
    subject: str
    predicate: str
    object: str

# EVOLVE-BLOCK-START

def predict(triples: list[Triple]) -> str:
    if not triples:
        return ""

    # Group triples by subject to build coherent, contextually rich sentences
    subject_groups = {}
    for triple in triples:
        subject_groups.setdefault(triple.subject, []).append(triple)

    sentences = []
    for subject, group in subject_groups.items():
        # Build descriptive phrases for this subject
        phrases = []
        for triple in group:
            if triple.predicate == "cityServed":
                phrases.append(f"serves the city of {triple.object}")
            elif triple.predicate == "country":
                phrases.append(f"located in {triple.object}")
            elif triple.predicate == "capital":
                phrases.append(f"is the capital of {triple.object}")
            elif triple.predicate == "elevationAboveTheSeaLevel":
                phrases.append(f"has an elevation of {triple.object} meters above sea level")
            elif triple.predicate == "location":
                phrases.append(f"located in {triple.object}")
            elif triple.predicate == "operatingOrganisation":
                phrases.append(f"operated by {triple.object}")
            elif triple.predicate == "runwayLength":
                phrases.append(f"has a runway length of {triple.object} meters")
            elif triple.predicate == "runwayName":
                phrases.append(f"has runway name {triple.object}")
            elif triple.predicate == "isPartOf":
                phrases.append(f"is part of {triple.object}")
            elif triple.predicate == "1stRunwayLengthFeet":
                phrases.append(f"has a first runway of {triple.object} feet long")
            elif triple.predicate == "1stRunwaySurfaceType":
                phrases.append(f"has a first runway surface type of {triple.object}")
            elif triple.predicate == "3rdRunwayLengthFeet":
                phrases.append(f"has a third runway of {triple.object} feet long")
            elif triple.predicate == "icaoLocationIdentifier":
                phrases.append(f"has ICAO identifier {triple.object}")
            elif triple.predicate == "locationIdentifier":
                phrases.append(f"has location identifier {triple.object}")
            elif triple.predicate == "elevationAboveTheSeaLevelInFeet":
                phrases.append(f"has an elevation of {triple.object} feet above sea level")
            elif triple.predicate == "iataLocationIdentifier":
                phrases.append(f"has IATA identifier {triple.object}")
            elif triple.predicate == "nativeName":
                phrases.append(f"also known as {triple.object}")
            elif triple.predicate == "leaderParty":
                phrases.append(f"led by the {triple.object}")
            elif triple.predicate == "language":
                phrases.append(f"has official language {triple.object}")
            elif triple.predicate == "owner":
                phrases.append(f"owned by {triple.object}")
            elif triple.predicate == "1stRunwayLengthMetre":
                phrases.append(f"has a first runway of {triple.object} meters long")
            elif triple.predicate == "4thRunwaySurfaceType":
                phrases.append(f"has a fourth runway surface type of {triple.object}")
            elif triple.predicate == "largestCity":
                phrases.append(f"has largest city {triple.object}")
            elif triple.predicate == "4thRunwayLengthFeet":
                phrases.append(f"has a fourth runway of {triple.object} feet long")
            elif triple.predicate == "elevationAboveTheSeaLevelInMetres":
                phrases.append(f"has an elevation of {triple.object} meters above sea level")
            elif triple.predicate == "administrativeArrondissement":
                phrases.append(f"located in the {triple.object}")
            elif triple.predicate == "officialLanguage":
                phrases.append(f"has official language {triple.object}")
            elif triple.predicate == "regionServed":
                phrases.append(f"serves the region of {triple.object}")
            else:
                phrases.append(f"{triple.predicate} {triple.object}")

        # Combine phrases into a single, natural-sounding sentence
        if len(phrases) == 1:
            sentences.append(f"{subject} {phrases[0]}.")
        else:
            # Use Oxford comma for better readability in complex lists
            sentence_part = ", ".join(phrases[:-1]) + (", and " if len(phrases) > 2 else " and ") + phrases[-1]
            sentences.append(f"{subject} {sentence_part}.")

    # Combine all subject sentences into one cohesive output
    return " ".join(sentences)

