from dataclasses import dataclass

@dataclass
class Triple:
    subject: str
    predicate: str
    object: str

# EVOLVE-BLOCK-START

def predict(triples: list[Triple]) -> str:
    # Group triples by subject for better sentence construction
    subject_groups = {}
    for triple in triples:
        if triple.subject not in subject_groups:
            subject_groups[triple.subject] = []
        subject_groups[triple.subject].append(triple)

    sentences = []

    for subject, triples_in_group in subject_groups.items():
        # Sort triples to group related facts together
        sorted_triples = sorted(triples_in_group, key=lambda x: x.predicate)

        # Build sentence parts
        parts = []

        # Handle cityServed specially to build connections
        city_served_triples = [t for t in sorted_triples if t.predicate == "cityServed"]
        country_triples = [t for t in sorted_triples if t.predicate == "country"]
        capital_triples = [t for t in sorted_triples if t.predicate == "capital"]
        location_triples = [t for t in sorted_triples if t.predicate == "location"]
        elevation_triples = [t for t in sorted_triples if t.predicate == "elevationAboveTheSeaLevel"]
        elevation_feet_triples = [t for t in sorted_triples if t.predicate == "elevationAboveTheSeaLevelInFeet"]
        elevation_metres_triples = [t for t in sorted_triples if t.predicate == "elevationAboveTheSeaLevelInMetres"]
        operating_org_triples = [t for t in sorted_triples if t.predicate == "operatingOrganisation"]
        runway_triples = [t for t in sorted_triples if t.predicate.startswith("runway")]

        # Build core sentence about subject
        main_sentence_parts = []

        # Handle city served relationships
        if city_served_triples:
            main_sentence_parts.append(f"serves the city of {city_served_triples[0].object}")

        # Handle country relationship
        if country_triples:
            main_sentence_parts.append(f"which is in {country_triples[0].object}")

        # Handle location
        if location_triples and not any("in" in part for part in main_sentence_parts):
            main_sentence_parts.append(f"located in {location_triples[0].object}")

        # Handle elevation
        elevations = elevation_triples + elevation_feet_triples + elevation_metres_triples
        if elevations:
            elevation_value = elevations[0].object
            unit = "meters" if elevations[0].predicate in ["elevationAboveTheSeaLevel", "elevationAboveTheSeaLevelInMetres"] else "feet"
            main_sentence_parts.append(f"with an elevation of {elevation_value} {unit}")

        # Handle operating organisation
        if operating_org_triples:
            main_sentence_parts.append(f"operated by {operating_org_triples[0].object}")

        # Add remaining triples that don't fit in main sentence structure
        other_triples = [t for t in sorted_triples
                        if t.predicate not in ["cityServed", "country", "capital", "location",
                                             "elevationAboveTheSeaLevel", "elevationAboveTheSeaLevelInFeet",
                                             "elevationAboveTheSeaLevelInMetres", "operatingOrganisation"]]

        # Combine all parts
        if main_sentence_parts:
            sentence = subject
            for i, part in enumerate(main_sentence_parts):
                if i == 0:
                    sentence += f" {part}"
                else:
                    sentence += f" and {part}"
            # Add other triples as additional clauses
            if other_triples:
                for ot in other_triples:
                    sentence += f", and {ot.predicate.replace('_', ' ')} {ot.object}"
            sentences.append(sentence + ".")
        else:
            # Fallback for cases with no special handling
            for triple in sorted_triples:
                if not sentence:
                    sentence += f"{triple.subject} {triple.predicate} {triple.object}. "
                else:
                    sentence += f"It {triple.predicate} {triple.object}. "
            sentences.append(sentence)

    return " ".join(sentences)

# EVOLVE-BLOCK-END
