from dataclasses import dataclass

@dataclass
class Triple:
    subject: str
    predicate: str
    object: str

# EVOLVE-BLOCK-START

def predict(triples: list[Triple]) -> str:
    sentence = ""
    for triple in triples:
        if triple.predicate in ["cityServed", "isPartOf"]:
            sentence += f"{triple.subject} is located in {triple.object}. "
        elif triple.predicate == "capital":
            sentence += f"{triple.subject}'s capital is {triple.object}. "
        elif triple.predicate in ["elevationAboveTheSeaLevel", "elevationAboveTheSeaLevelInFeet", "elevationAboveTheSeaLevelInMetres"]:
            sentence += f"{triple.subject}'s elevation is {triple.object}. "
        elif triple.predicate in ["runwayLength", "4thRunwayLengthFeet", "4thRunwayLengthFeet"]:
            sentence += f"{triple.subject} has a runway length of {triple.object} feet. "
        elif triple.predicate in ["runwayName", "3rdRunwayLengthFeet", "1stRunwayLengthMetre"]:
            sentence += f"{triple.subject}'s runway is named {triple.object}. "
        elif triple.predicate in ["operatingOrganisation", "owner"]:
            sentence += f"{triple.subject}'s operator is {triple.object}. "
        elif triple.predicate == "language":
            sentence += f"{triple.subject}'s official language is {triple.object}. "
        elif triple.predicate in ["leaderParty", "leaderTitle"]:
            sentence += f"{triple.subject} is led by the {triple.object}. "
        else:
            sentence += f"{triple.subject} is a {triple.predicate}. "
    return sentence

