from dataclasses import dataclass

@dataclass
class Triple:
    subject: str
    predicate: str
    object: str

# EVOLVE-BLOCK-START

def predict(triples: list[Triple]) -> str:
    # Create a dictionary of subject-predicate pairs
    subjects = {}
    for triple in triples:
        if triple.subject not in subjects:
            subjects[triple.subject] = []
        subjects[triple.subject].append(triple.predicate)

    # Generate a sentence by combining the subject-predicate pairs
    sentence = ""
    for subject, predicates in subjects.items():
        sentence += f"{subject} is a {predicates[0]}"
        for predicate in predicates[1:]:
            sentence += f", {predicate}"
        sentence += ". "
    return sentence

