from dataclasses import dataclass

@dataclass
class Triple:
    subject: str
    predicate: str
    object: str

# EVOLVE-BLOCK-START

def predict(triples: list[Triple]) -> str:
    sentence = ""
    subject = None
    related_info = []

    for triple in triples:
        if subject is None:
            subject = triple.subject
            if triple.predicate == "cityServed":
                sentence += f"{triple.subject} serves the city of {triple.object}. "
            elif triple.predicate == "country":
                sentence += f"{triple.subject} is located in {triple.object}. "
            elif triple.predicate == "capital":
                sentence += f"{triple.subject} is the capital of {triple.object}. "
            else:
                sentence += f"{triple.subject} {triple.predicate} {triple.object}. "
        else:
            if triple.subject == subject:
                related_info.append((triple.predicate, triple.object))

    if related_info:
        sentence += "Additionally, "
        for i, (predicate, obj) in enumerate(related_info):
            if predicate == "elevationAboveTheSeaLevel":
                sentence += f"it has an elevation of {obj} meters above sea level"
            elif predicate == "location":
                sentence += f"it is located in {obj}"
            elif predicate == "operatingOrganisation":
                sentence += f"it is operated by {obj}"
            elif predicate == "runwayLength":
                sentence += f"the runway length is {obj} meters"
            elif predicate == "runwayName":
                sentence += f"the runway name is {obj}"
            elif predicate == "1stRunwayLengthFeet":
                sentence += f"the first runway is {obj} feet long"
            elif predicate == "1stRunwaySurfaceType":
                sentence += f"the first runway surface type is {obj}"
            elif predicate == "icaoLocationIdentifier":
                sentence += f"the ICAO identifier is {obj}"
            elif predicate == "iataLocationIdentifier":
                sentence += f"the IATA identifier is {obj}"
            elif predicate == "nativeName":
                sentence += f"it is also known as {obj}"
            else:
                sentence += f"{predicate} is {obj}"
            if i < len(related_info) - 1:
                sentence += ", "
        sentence += "."

    return sentence

# EVOLVE-BLOCK-END
