def predict(triples: list[Triple]) -> str:
    # Group triples by subject to build richer, more coherent sentences
    subject_groups = {}
    for triple in triples:
        if triple.subject not in subject_groups:
            subject_groups[triple.subject] = []
        subject_groups[triple.subject].append(triple)

    sentences = []
    for subject, triples in subject_groups.items():
        parts = []
        city_served = None
        elevation_meters = None
        elevation_feet = None
        location = None
        operating_org = None
        runway_length = None
        runway_name = None
        owner = None
        icao_id = None
        iata_id = None
        native_name = None
        country = None
        capital = None
        admin_arrondissement = None
        official_language = None
        largest_city = None
        region_served = None

        for triple in triples:
            if triple.predicate == "cityServed":
                city_served = triple.object
            elif triple.predicate == "elevationAboveTheSeaLevel":
                elevation_meters = triple.object
            elif triple.predicate == "elevationAboveTheSeaLevelInFeet":
                elevation_feet = triple.object
            elif triple.predicate == "location":
                location = triple.object
            elif triple.predicate == "operatingOrganisation":
                operating_org = triple.object
            elif triple.predicate == "runwayLength":
                runway_length = triple.object
            elif triple.predicate == "runwayName":
                runway_name = triple.object
            elif triple.predicate == "owner":
                owner = triple.object
            elif triple.predicate == "icaoLocationIdentifier":
                icao_id = triple.object
            elif triple.predicate == "iataLocationIdentifier":
                iata_id = triple.object
            elif triple.predicate == "nativeName":
                native_name = triple.object
            elif triple.predicate == "country":
                country = triple.object
            elif triple.predicate == "capital":
                capital = triple.object
            elif triple.predicate == "administrativeArrondissement":
                admin_arrondissement = triple.object
            elif triple.predicate == "officialLanguage":
                official_language = triple.object
            elif triple.predicate == "largestCity":
                largest_city = triple.object
            elif triple.predicate == "regionServed":
                region_served = triple.object

        # Build a coherent sentence for this subject using more natural phrasing and logical flow
        sentence_parts = []

        # Start with the subject and its primary function
        if city_served:
            sentence_parts.append(f"{subject} serves the city of {city_served}")

        # Add location context
        if location:
            sentence_parts.append(f"located in {location}")
        if country:
            sentence_parts.append(f"in {country}")
        if admin_arrondissement:
            sentence_parts.append(f"within the {admin_arrondissement}")

        # Add capital if available
        if capital:
            sentence_parts.append(f"where the capital is {capital}")

        # Add elevation
        if elevation_meters:
            sentence_parts.append(f"with an elevation of {elevation_meters} meters above sea level")
        elif elevation_feet:
            sentence_parts.append(f"with an elevation of {elevation_feet} feet above sea level")

        # Add operational details
        if operating_org:
            sentence_parts.append(f"operated by {operating_org}")
        if owner:
            sentence_parts.append(f"owned by {owner}")

        # Add runway information
        if runway_length:
            sentence_parts.append(f"with a runway length of {runway_length} meters")
        if runway_name:
            sentence_parts.append(f"and runway name {runway_name}")

        # Add identifiers
        if icao_id:
            sentence_parts.append(f"ICAO identifier {icao_id}")
        if iata_id:
            sentence_parts.append(f"IATA identifier {iata_id}")

        # Add alternative names
        if native_name:
            sentence_parts.append(f"also known as {native_name}")

        # Add language and regional info
        if official_language:
            sentence_parts.append(f"with {official_language} as the official language")
        if largest_city:
            sentence_parts.append(f"the largest city is {largest_city}")
        if region_served:
            sentence_parts.append(f"serving the region of {region_served}")

        # Combine parts into a single sentence with proper grammar
        if sentence_parts:
            # Use commas and "and" for complex structure
            sentence = f"{subject} {', '.join(sentence_parts[:-1])} and {sentence_parts[-1]}."
            sentences.append(sentence)
        else:
            # Fallback if no info to include
            sentences.append(f"{subject} is a location with no available details.")

        # Combine parts into a single sentence
        if parts:
            sentence = ", ".join(parts)
            if not sentence.endswith("."):
                sentence += "."
            sentences.append(sentence)

    return " ".join(sentences)

