def predict(triples: list[Triple]) -> str:
    # Group triples by subject to build richer, more coherent sentences
    subject_groups = {}
    for triple in triples:
        if triple.subject not in subject_groups:
            subject_groups[triple.subject] = []
        subject_groups[triple.subject].append(triple)

    sentences = []
    for subject, triples in subject_groups.items():
        parts = []
        city_served = None
        elevation_meters = None
        elevation_feet = None
        location = None
        operating_org = None
        runway_length = None
        runway_name = None
        owner = None
        icao_id = None
        iata_id = None
        native_name = None
        country = None
        capital = None
        admin_arrondissement = None
        official_language = None
        largest_city = None
        region_served = None

        for triple in triples:
            if triple.predicate == "cityServed":
                city_served = triple.object
            elif triple.predicate == "elevationAboveTheSeaLevel":
                elevation_meters = triple.object
            elif triple.predicate == "elevationAboveTheSeaLevelInFeet":
                elevation_feet = triple.object
            elif triple.predicate == "location":
                location = triple.object
            elif triple.predicate == "operatingOrganisation":
                operating_org = triple.object
            elif triple.predicate == "runwayLength":
                runway_length = triple.object
            elif triple.predicate == "runwayName":
                runway_name = triple.object
            elif triple.predicate == "owner":
                owner = triple.object
            elif triple.predicate == "icaoLocationIdentifier":
                icao_id = triple.object
            elif triple.predicate == "iataLocationIdentifier":
                iata_id = triple.object
            elif triple.predicate == "nativeName":
                native_name = triple.object
            elif triple.predicate == "country":
                country = triple.object
            elif triple.predicate == "capital":
                capital = triple.object
            elif triple.predicate == "administrativeArrondissement":
                admin_arrondissement = triple.object
            elif triple.predicate == "officialLanguage":
                official_language = triple.object
            elif triple.predicate == "largestCity":
                largest_city = triple.object
            elif triple.predicate == "regionServed":
                region_served = triple.object

        # Build a coherent sentence for this subject with improved logical flow and natural language
        sentence_parts = []

        # Primary fact: city served, location, or country
        if city_served:
            sentence_parts.append(f"serves the city of {city_served}")
        elif location:
            sentence_parts.append(f"is located in {location}")
        elif country:
            sentence_parts.append(f"is in {country}")

        # Add geographic and administrative context
        if country:
            if not any(p in ["cityServed", "location"] for p in [t.predicate for t in triples]):
                sentence_parts.append(f"in {country}")
            if capital:
                sentence_parts.append(f"where the capital is {capital}")
        if admin_arrondissement:
            sentence_parts.append(f"within the {admin_arrondissement}")

        # Add elevation (prefer meters if available, otherwise feet)
        if elevation_meters:
            sentence_parts.append(f"at an elevation of {elevation_meters} meters above sea level")
        elif elevation_feet:
            sentence_parts.append(f"at an elevation of {elevation_feet} feet above sea level")

        # Add operational details
        if operating_org:
            sentence_parts.append(f"operated by {operating_org}")
        if owner:
            sentence_parts.append(f"owned by {owner}")

        # Add runway information with proper structure
        runway_info = []
        for i in [1, 2, 3, 4, 5]:
            length_key = f"{i}thRunwayLengthFeet"
            length_m_key = f"{i}thRunwayLengthMetre"
            surface_key = f"{i}thRunwaySurfaceType"
            number_key = f"{i}thRunwayNumber"

            length_feet = None
            length_metre = None
            surface = None
            number = None

            for triple in triples:
                if triple.predicate == length_key:
                    length_feet = triple.object
                elif triple.predicate == length_m_key:
                    length_metre = triple.object
                elif triple.predicate == surface_key:
                    surface = triple.object
                elif triple.predicate == number_key:
                    number = triple.object

            if number or length_feet or length_metre or surface:
                parts = []
                if number:
                    parts.append(f"runway {number}")
                if length_feet:
                    parts.append(f"{length_feet} feet long")
                if length_metre:
                    parts.append(f"{length_metre} meters long")
                if surface:
                    parts.append(f"with a {surface} surface")
                runway_info.append(" ".join(parts))

        # Consolidate runway details: prioritize numbered runways, then fallback to general info
        if runway_info:
            if len(runway_info) == 1:
                sentence_parts.append(f"with {runway_info[0]}")
            else:
                runway_str = ", ".join(runway_info[:-1]) + f" and {runway_info[-1]}"
                sentence_parts.append(f"with runways: {runway_str}")
        elif runway_length:
            sentence_parts.append(f"with a runway length of {runway_length} meters")
        elif runway_name:
            sentence_parts.append(f"and runway name {runway_name}")
        elif any(t.predicate == "runwaySurfaceType" for t in triples):
            for triple in triples:
                if triple.predicate == "runwaySurfaceType":
                    sentence_parts.append(f"with a {triple.object} runway surface")

        # Consolidate all runway info
        if runway_info:
            if len(runway_info) == 1:
                sentence_parts.append(f"with {runway_info[0]}")
            else:
                runway_str = ", ".join(runway_info[:-1]) + f" and {runway_info[-1]}"
                sentence_parts.append(f"with runways: {runway_str}")

        # Add identifiers
        identifiers = []
        if icao_id:
            identifiers.append(f"ICAO {icao_id}")
        if iata_id:
            identifiers.append(f"IATA {iata_id}")
        if identifiers:
            sentence_parts.append(f"with identifiers: {', '.join(identifiers)}")

        # Add other relevant facts
        if native_name:
            sentence_parts.append(f"also known as {native_name}")
        if official_language:
            sentence_parts.append(f"where the official language is {official_language}")
        if largest_city:
            sentence_parts.append(f"with {largest_city} as its largest city")
        if region_served:
            sentence_parts.append(f"serving the region of {region_served}")

        # Combine all parts into a single, natural-sounding sentence
        if sentence_parts:
            sentence = f"{subject} {' and '.join(sentence_parts)}."
            sentences.append(sentence)
        else:
            sentences.append(f"{subject} is a location with no available details.")

    return " ".join(sentences)

