from dataclasses import dataclass

@dataclass
class Triple:
    subject: str
    predicate: str
    object: str

# EVOLVE-BLOCK-START

def predict(triples: list[Triple]) -> str:
    # Group triples by subject to build more coherent, multi-fact sentences
    subject_groups = {}
    for triple in triples:
        subject_groups.setdefault(triple.subject, []).append(triple)

    sentences = []
    for subject, subject_triples in subject_groups.items():
        sentence_parts = []
        for triple in subject_triples:
            if triple.predicate == "cityServed":
                sentence_parts.append(f"serves the city of {triple.object}")
            elif triple.predicate == "country":
                sentence_parts.append(f"is in {triple.object}")
            elif triple.predicate == "capital":
                sentence_parts.append(f"the capital is {triple.object}")
            elif triple.predicate == "elevationAboveTheSeaLevel":
                sentence_parts.append(f"has an elevation of {triple.object} meters above sea level")
            elif triple.predicate == "location":
                sentence_parts.append(f"is located in {triple.object}")
            elif triple.predicate == "operatingOrganisation":
                sentence_parts.append(f"is operated by {triple.object}")
            elif triple.predicate == "runwayLength":
                sentence_parts.append(f"has a runway length of {triple.object} meters")
            elif triple.predicate == "runwayName":
                sentence_parts.append(f"has a runway named {triple.object}")
            elif triple.predicate == "isPartOf":
                sentence_parts.append(f"is part of {triple.object}")
            elif triple.predicate == "1stRunwayLengthFeet":
                sentence_parts.append(f"has a first runway of {triple.object} feet long")
            elif triple.predicate == "1stRunwaySurfaceType":
                sentence_parts.append(f"has a first runway surface type of {triple.object}")
            elif triple.predicate == "3rdRunwayLengthFeet":
                sentence_parts.append(f"has a third runway of {triple.object} feet long")
            elif triple.predicate == "icaoLocationIdentifier":
                sentence_parts.append(f"has ICAO location identifier {triple.object}")
            elif triple.predicate == "locationIdentifier":
                sentence_parts.append(f"has location identifier {triple.object}")
            elif triple.predicate == "elevationAboveTheSeaLevelInFeet":
                sentence_parts.append(f"has an elevation of {triple.object} feet above sea level")
            elif triple.predicate == "iataLocationIdentifier":
                sentence_parts.append(f"has IATA location identifier {triple.object}")
            elif triple.predicate == "nativeName":
                sentence_parts.append(f"also known as {triple.object}")
            elif triple.predicate == "leaderParty":
                sentence_parts.append(f"is led by the {triple.object}")
            elif triple.predicate == "language":
                sentence_parts.append(f"has official language {triple.object}")
            elif triple.predicate == "owner":
                sentence_parts.append(f"is owned by {triple.object}")
            elif triple.predicate == "1stRunwayLengthMetre":
                sentence_parts.append(f"has a first runway of {triple.object} meters long")
            elif triple.predicate == "4thRunwaySurfaceType":
                sentence_parts.append(f"has a fourth runway surface type of {triple.object}")
            elif triple.predicate == "largestCity":
                sentence_parts.append(f"has largest city {triple.object}")
            elif triple.predicate == "4thRunwayLengthFeet":
                sentence_parts.append(f"has a fourth runway of {triple.object} feet long")
            elif triple.predicate == "elevationAboveTheSeaLevelInMetres":
                sentence_parts.append(f"has an elevation of {triple.object} meters above sea level")
            elif triple.predicate == "administrativeArrondissement":
                sentence_parts.append(f"is in the {triple.object}")
            elif triple.predicate == "officialLanguage":
                sentence_parts.append(f"has official language {triple.object}")
            elif triple.predicate == "regionServed":
                sentence_parts.append(f"serves the region of {triple.object}")
            else:
                sentence_parts.append(f"{triple.predicate} {triple.object}")

        # Join sentence parts with commas and add subject
        if sentence_parts:
            # Use Oxford comma for better readability in lists of three or more items
            if len(sentence_parts) > 2:
                sentence = f"{subject} {', '.join(sentence_parts[:-1])}, and {sentence_parts[-1]}."
            else:
                sentence = f"{subject} {' '.join(sentence_parts)}."
            sentences.append(sentence)

    return " ".join(sentences)

